import React, { useContext } from 'react';
import { Container, Introduction, Offers } from '../Offers/Offers';
import { colorContext } from '../../Context/ColorContextProvider';
import OfferingsSchema from '../Offers/OfferSchema';

const WhyUs = () => {
    const { config } = useContext(colorContext);
    const data = {

        1: {
            title: '1',
            content: 'ما انواع شکلات‌های سفارشی و دست‌ساز را مطابق با سلیقه شما تولید می‌کنیم. طعم و طرح دلخواهتان را انتخاب کنید.',
            path: '/services'
        },
        2: {
            title: '2',
            content: 'باکس‌های شکلات لوکس و شکیل، مناس ب برای مناسب ت‌هامناسبت‌ه امناسبت‌هامن اسبت‌هامناس بت‌هامناسب ت‌هامناسب ت‌هامناس بت‌هامنا سبت‌هامناسب ت‌هامناسب ت‌هامنا سبت‌هامناسبت‌ ها مناسبت‌هامناسبت‌هامناسبت‌ها و هدیه‌های خاص.',
            path: '/services'
        },
        3: {
            title: 'شکلات‌های ویژه فصلی',
            content: 'مجموعه‌ای از شکلات‌های ویژه مخصوص تعطیلات و فصل‌ها، با طعم و تزئینات فصلی.',
            path: '/services'
        },
    };
    

    return (
        <Container>
            <Introduction theme={config.offering}>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>در [نام فروشگاه شما]، ما تخصصی در تولید شکلات‌های سفارشی و هدیه‌های خاص داریم.<br />با ما طعم و ظرافت را تجربه کنید.</p>
            </Introduction>
            <Offers>
            <OfferingsSchema
                    title={data[1].title}
                    content={data.chocolateBouquets.content}
                    goto={data.chocolateBouquets.path}
                />
                <OfferingsSchema
                    title={data[2].chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
                <OfferingsSchema
                    title={data[3].chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
            </Offers>
        </Container>

    );
};

export default WhyUs;
