import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

// Create a Context for CSRF Token
const CsrfTokenContext = createContext();

// Custom hook to use CSRF Token context
export const useCsrfToken = () => {
    return useContext(CsrfTokenContext);
};

// Provider component to wrap your app
export const CsrfTokenProvider = ({ children }) => {
    const [csrfToken, setCsrfToken] = useState('');

    useEffect(() => {
        // Fetch CSRF token from backend on initial load
        axios.get('http://localhost:8080/api/csrf-token')
            .then(response => {
                setCsrfToken(response.data.csrfToken);  // Store the token in state
            })
            .catch(error => {
                console.error('Error fetching CSRF token:', error);
            });
    }, []);

    return (
        <CsrfTokenContext.Provider value={csrfToken}>
            {children}
        </CsrfTokenContext.Provider>
    );
};
