import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#1C1C1C', // Deep black for a timeless and luxurious background
        logoContainer: '#B29555', // Warm gold for elegance
        h1: '#F8ECC2', // Champagne gold for subtle luxury
        hamburger: '#704214', // Rich brown for a grounded look
        hamDiv: '#D9B99B', // Soft rose gold
        links: '#D5A021', // Vibrant gold for links
        linksPhone: '#B29555',
        linkLi: '#704214',
        linkA: '#D9B99B',
        iconContainer: '#B29555',
        iconA: '#F8ECC2',
        profile: '#D5A021',
        pulse: '#B29555',
        cart: '#704214',
    },
    landing: {
        bg: '#1C1C1C', // Black to set a dramatic tone
        left: '#D9B99B', // Rose gold for exclusivity
        textLeft: '#FFFFFF', // Pure white for clarity
        textLeftSpan: '#B29555',
        center: '#B29555', // Luxurious gold as a focal point
        shopLink: '#D5A021',
        centerButtons: '#704214',
        centerButtonsLink: '#FFFFFF',
        centerButton2: '#D9B99B',
        centerButton2Link: '#FFFFFF',
        storeButton: '#704214',
        aboutButton: '#B29555',
        right: '#D5A021',
        textRight: '#FFFFFF',
        textRightSpan: '#B29555',
    },
    offeringSchema: {
        bg: 'rgba(28, 28, 28, 0.9)', // Transparent black for mystery
        bgHover: 'rgba(28, 28, 28, 1)',
        icon: '#B29555', // Gold for standout icons
        iconHover: '#D5A021',
        title: '#FFFFFF', // White for clean contrast
        description: '#D9B99B', // Rose gold accents
        styledLink: '#B29555',
        styledLinkHover: '#704214',
    },
    offering: {
        bg: 'rgba(28, 28, 28, 0.8)',
        introduction: 'rgba(28, 28, 28, 0.7)',
        introH1: '#B29555',
        introH2: '#D5A021',
        introP: '#D9B99B',
        offers: 'rgba(43, 43, 43, 0.05)', // Soft charcoal for subtle depth
    },
    contact: {
        bg: '#1C1C1C',
        title: '#D5A021', // Gold tones for headers
        description: '#D9B99B',
        socialLinks: '#704214',
        socialLink: '#B29555',
    },
    clientCounter: {
        bg: 'rgba(28, 28, 28, 0.9)',
        countWrapper: 'rgba(28, 28, 28, 0.8)',
        number: '#D5A021',
        counter: '#704214',
    },
    about: {
        bg: '#1C1C1C',
        aboutSection: '#B29555',
        aboutSectionH1: '#D5A021',
        aboutSectionH2: '#704214',
        aboutSectionP: '#D9B99B',
        faq: '#F8ECC2', // Champagne highlights
        dropdown: '#D5A021',
        dropdownH1: '#B29555',
        answer: '#704214',
        answerP: '#D9B99B',
    },
    footer: {
        bg: '#1C1C1C',
        footWrapper: '#1C1C1C',
        section: '#704214',
        companyTitile: '#D5A021',
        companyDescription: '#D9B99B',
        sectionTitile: '#B29555',
        styledLink: '#D5A021',
        finalSection: '#704214',
        finalText: '#F8ECC2',
        finalLink: '#D5A021',
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
