import React, { useContext } from 'react';
import { colorContext } from '../../Context/ColorContextProvider';
import styled from 'styled-components';
import ServicesCard from './Services-Card';

const Services = () => {

    const { config } = useContext(colorContext);

    const services = [
        { IconImg: 1, title: 'VPS', goTo: '/service/vps' },
        { IconImg: 2, title: 'INDICATORS', goTo: '/service/indicator' },
        { IconImg: 3, title: 'VPNS', goTo: '/service/vpns' },
    ];

    return (
        <Container theme={config}>
            <Header theme={config}>
                خدمات ما
                <br />
                لطفا یک سرویس را انتخاب کنید
            </Header>
            <ServicesContainer theme={config}>
                <ServicesCard CardData={services[0]} />
                <ServicesCard CardData={services[1]} />
                <ServicesCard CardData={services[2]} />
            </ServicesContainer>
        </Container>
    );
};

export default Services;


// Styled Components

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 100vh;
  width: 100;
  /* background-color: ${(props) => props.bg}; */
  overflow: hidden;
  padding-top: 80px;
`;

const Header = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
  width: 100%;
  background-color: red;
  text-align: center;
`;


const ServicesContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
  width: 100%;
  background-color: red;
  text-align: center;
`;
