import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#FFF8F0', // Soft cream background for a warm, chocolate-inspired feel
        logoContainer: '#4B4B4B', // Dark gray for logo container to add sophistication
        h1: '#4B4B4B', // Dark gray for headings, ensuring readability and elegance
        hamburger: '#6F4F37', // A rich brownish tone for the hamburger icon to tie with the chocolate theme
        hamDiv: '#F5E6D6', // Light cream background for the hamburger menu container, matching the theme
        links: '#4B4B4B', // Dark gray for navigation links, maintaining legibility and style
        linksPhone: '#FFF8F0', // Light cream for phone version links for a uniform look
        linkLi: '#4B4B4B', // Dark gray for list items in the navigation
        linkA: '#5A2A2A', // Deep chocolate brown for link text, adding a bold accent
        iconContainer: '#4B4B4B', // Matching dark gray for the icon container
        iconA: '#5A2A2A', // Chocolate brown for icon links, reinforcing the theme
        profile: '#5A2A2A', // Chocolate brown for profile icon for consistency
        pulse: '#F4A300', // Warm orange/yellow pulse effect for interactivity
        cart: '#5A2A2A', // Deep chocolate brown for the cart icon, tying everything together
    }
}

const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
