import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaGift, FaLeaf, FaCandyCane, FaChalkboardTeacher, FaSeedling } from 'react-icons/fa';

const OfferingsSchema = ({ imgSrc, title, content, goto }) => {
    const [hover, setHover] = useState(false);
    const { config } = useContext(colorContext); // Accessing the context

    // Icon selection based on Persian title
    const renderIcon = () => {
        switch (title) {
            case 'سفارش شکلات سفارشی':
                return <Icon as={FaCandyCane} color={config.offeringSchema.icon} />;
            case 'باکس‌های هدیه':
                return <Icon as={FaGift} color={config.offeringSchema.icon} />;
            case 'شکلات‌های ویژه فصلی':
                return <Icon as={FaLeaf} color={config.offeringSchema.icon} />;
            case 'دسته گل‌های شکلاتی':
                return <Icon as={FaSeedling} color={config.offeringSchema.icon} />;
            case 'کارگاه‌های آموزشی شکلات‌سازی':
                return <Icon as={FaChalkboardTeacher} color={config.offeringSchema.icon} />;
            default:
                return <Icon as={FaGift} color={config.offeringSchema.icon} />;
        }
    };

    return (
        <Container
            onMouseEnter={() => setHover(true)}
            onMouseLeave={() => setHover(false)}
            style={{ backgroundColor: config.offeringSchema.bg, borderColor: hover ? config.offeringSchema.styledLink : '#eaeaea' }}
        >
            {renderIcon()}
            <Title style={{ color: config.offeringSchema.title }}>{title}</Title>
            <Description style={{ color: config.offeringSchema.description }}>{content}</Description>
            <StyledLink to={goto} style={{ color: config.offeringSchema.styledLink }}>
                بیشتر بدانید
            </StyledLink>
        </Container>
    );
};

export default OfferingsSchema;

// Styled Components
const Container = styled.div`
    width: 90%;
    max-width: 300px;
    height: 250px;
    overflow: hidden;
    word-wrap: wrap;
    border: 2px solid #eaeaea;
    border-radius: 20px;
    margin: 20px auto;
    padding: 20px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: transform 0.3s ease, box-shadow 0.3s ease, background-color 0.3s ease;
    text-align: center;

    &:hover {
        transform: scale(1.05);
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.15);
    }

    @media screen and (max-width: 991px) {
        width: 80%;
    }
`;

const Icon = styled.div`
    font-size: 120px;
    margin-bottom: 10px;
    transition: color 0.3s ease;
`;

const Title = styled.h1`
    font-size: 22px;
    margin: 10px 0;
`;

const Description = styled.p`
    font-size: 16px;
    margin: 15px 0;
    width: 100%;
    overflow: hidden;
    word-wrap: break-word;
    height: 250px;
    line-height: 1.6;
    padding: 15px;
`;

const StyledLink = styled(Link)`
    font-size: 16px;
    text-decoration: none;
    font-weight: bold;
    margin-top: 15px;
    transition: color 0.3s ease;

    &:hover {
        text-decoration: underline;
    }
`;
