import React from 'react';
import styled from 'styled-components';
import OfferingsSchema from './OfferSchema';

// Data for the offerings
const data = {
    customChocolates: {
        title: 'سفارش شکلات سفارشی',
        content: 'ما انواع شکلات‌های سفارشی و دست‌ساز را مطابق با سلیقه شما تولید می‌کنیم. طعم و طرح دلخواهتان را انتخاب کنید.',
        path: '/services'
    },
    giftBoxes: {
        title: 'باکس‌های هدیه',
        content: 'باکس‌های شکلات لوکس و شکیل، مناسب برای مناسبت‌ها و هدیه‌های خاص.',
        path: '/services'
    },
    seasonalSpecials: {
        title: 'شکلات‌های ویژه فصلی',
        content: 'مجموعه‌ای از شکلات‌های ویژه مخصوص تعطیلات و فصل‌ها، با طعم و تزئینات فصلی.',
        path: '/services'
    },
    chocolateBouquets: {
        title: 'دسته گل‌های شکلاتی',
        content: 'با دسته گل‌های شکلاتی ما، یک هدیه خلاقانه و خوشمزه را تجربه کنید. مناسب برای هر مناسبت.',
        path: '/services'
    },
    chocolateWorkshops: {
        title: 'کارگاه‌های آموزشی شکلات‌سازی',
        content: 'کارگاه‌های ما شما را با اصول و فنون شکلات‌سازی آشنا می‌کند. لذت تجربه ساخت شکلات را با ما داشته باشید.',
        path: '/services'
    }
};

// Styled Components
const Container = styled.div`
    width: 100%;
    height: fit-content;
    background-color: #ffffff;
    margin-top: 250px;
    padding-bottom: 70px;
    overflow: hidden;
`;

const Introduction = styled.div`
    width: 100%;
    text-align: right;
    height: fit-content;
    background-color: #f8f9fc;
    padding: 40px 100px;
    margin-bottom: 100px;

    h1 {
        font-size: 16px;
        color: #8E44AD;
        padding: 20px 0px 0px 20px;
        line-height: 1.2;
    }

    h2 {
        font-size: 36px;
        color: #401e4f;
        padding: 20px 0px 0px 20px;
        line-height: 1.3;
    }

    p {
        padding: 20px;
        font-size: 18px;
        color: #777;
        line-height: 1.8;
        max-width: 900px;
        width: 100%;
        margin: 10px;
    }
`;

const OffersSection = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 18px;
    transition: 1.5s ease;
    margin-top: 40px;

    @media screen and (min-width: 991px) {
        width: 90%;
        margin: auto;
        flex-wrap: wrap;
        flex-direction: row;
        justify-content: space-between;
    }
`;

const OfferItem = styled.div`
    background-color: #ffffff;
    padding: 20px;
    border-radius: 12px;
    box-shadow: 0px 6px 15px rgba(0, 0, 0, 0.1);
    width: calc(33.33% - 20px);
    margin: 10px;
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    transition: background-color 0.3s ease, box-shadow 0.3s ease;

    &:hover {
        background-color: #d596f042;
        color: #3b4a61;
        box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.15);
        transform: translateY(-5px);
    }

    @media screen and (max-width: 375px) {
        width: 90%;
        padding: 15px;
        margin: 10px 0;
    }
`;

const Offers = () => {
    return (
        <Container>
            <Introduction>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>در [نام فروشگاه شما]، ما تخصصی در تولید شکلات‌های سفارشی و هدیه‌های خاص داریم.<br />با ما طعم و ظرافت را تجربه کنید.</p>
            </Introduction>
            <OffersSection>
                <OfferItem>
                    <OfferingsSchema
                        imgSrc={data.customChocolates.img}
                        title={data.customChocolates.title}
                        content={data.customChocolates.content}
                        goto={data.customChocolates.path}
                    />
                </OfferItem>

                <OfferItem>
                    <OfferingsSchema
                        imgSrc={data.giftBoxes.img}
                        title={data.giftBoxes.title}
                        content={data.giftBoxes.content}
                        goto={data.giftBoxes.path}
                    />
                </OfferItem>

                <OfferItem>
                    <OfferingsSchema
                        imgSrc={data.seasonalSpecials.img}
                        title={data.seasonalSpecials.title}
                        content={data.seasonalSpecials.content}
                        goto={data.seasonalSpecials.path}
                    />
                </OfferItem>

                <OfferItem>
                    <OfferingsSchema
                        imgSrc={data.chocolateBouquets.img}
                        title={data.chocolateBouquets.title}
                        content={data.chocolateBouquets.content}
                        goto={data.chocolateBouquets.path}
                    />
                </OfferItem>

                <OfferItem>
                    <OfferingsSchema
                        imgSrc={data.chocolateWorkshops.img}
                        title={data.chocolateWorkshops.title}
                        content={data.chocolateWorkshops.content}
                        goto={data.chocolateWorkshops.path}
                    />
                </OfferItem>
            </OffersSection>
        </Container>
    );
};

export default Offers;
