import React, { useEffect, useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import axios from 'axios';
import { FaBox, FaUserCircle, FaDollarSign, FaShoppingCart } from 'react-icons/fa';



// InfoBox Component
const InfoBox = ({ icon: Icon, title, data, loading, onClick }) => (
    <InfoBoxContainer onClick={onClick}>
        <Icon size={40} />
        <Title>{title}</Title>
        {loading ? (
            <LoadingText>در حال بارگذاری...</LoadingText>
        ) : (
            <UserCountText>{data !== null ? data : 'خطا در دریافت اطلاعات'}</UserCountText>
        )}
    </InfoBoxContainer>
);

// Overview Component
const Overview = ({ selectFunction }) => {
    const [dataCounts, setDataCounts] = useState({
        userCount: null,
        productsCount: null,
        ordersCount: 2, // Placeholder count for demo purposes
        totalProfit: 2, // Placeholder value
    });
    const [loadingStates, setLoadingStates] = useState({
        users: true,
        products: true,
    });
    const [responseMsg, setResponseMsg] = useState('');

    const fetchData = async (url, dataKey, loadingKey) => {
        try {
            const response = await axios.get(url, { withCredentials: true });
            if (response.status === 200 && response.data) {
                setDataCounts((prev) => ({ ...prev, [dataKey]: response.data[dataKey] }));
            }
        } catch (error) {
            if (error.response) {
                setResponseMsg(error.response.data.message || 'یک خطای غیرمنتظره رخ داده است.');
            } else {
                setResponseMsg('خطای شبکه. لطفا اتصال خود را بررسی کنید.');
            }
        } finally {
            setLoadingStates((prev) => ({ ...prev, [loadingKey]: false }));
        }
    };

    useEffect(() => {
        fetchData('http://localhost:8080/api/get-usercount', 'userCount', 'users');
        fetchData('http://localhost:8080/api/get-productcount', 'productsCount', 'products');
    }, []);

    return (
        <>
            <GlobalStyle />
            <OverviewContainer>
                <OverviewTitle>بررسی کلی</OverviewTitle>
                {responseMsg && <OverviewTitle>{responseMsg}</OverviewTitle>}
                <InfoBoxesContainer>
                    <InfoBox
                        icon={FaUserCircle}
                        title="تعداد کاربران"
                        data={dataCounts.userCount}
                        loading={loadingStates.users}
                    />
                    <InfoBox
                        icon={FaBox}
                        title="تعداد محصولات"
                        data={dataCounts.productsCount}
                        loading={loadingStates.products}
                        onClick={() => selectFunction('products')}
                    />
                    <InfoBox
                        icon={FaDollarSign}
                        title="سود کل"
                        data={dataCounts.totalProfit}
                        loading={false}
                        onClick={() => selectFunction('orders')}
                    />
                    <InfoBox
                        icon={FaShoppingCart}
                        title="تعداد سفارشات"
                        data={dataCounts.ordersCount}
                        loading={false}
                        onClick={() => selectFunction('orders')}
                    />
                </InfoBoxesContainer>
            </OverviewContainer>
        </>
    );
};

export default Overview;
