import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaGift, FaLeaf, FaCandyCane, FaChalkboardTeacher, FaSeedling } from 'react-icons/fa';
import { colorContext } from '../../Context/ColorContextProvider';

const OfferingsSchema = ({ imgSrc, title, content, goto }) => {
    const [hover, setHover] = useState(false);
    const { config } = useContext(colorContext);
    // Icon selection based on Persian title
    const renderIcon = () => {
        switch (title) {
            case 'سفارش شکلات سفارشی':
                return <Icon theme={config.offeringSchema} as={FaCandyCane} />;
            case 'باکس‌های هدیه':
                return <Icon theme={config.offeringSchema} as={FaGift} />;
            case 'شکلات‌های ویژه فصلی':
                return <Icon theme={config.offeringSchema} as={FaLeaf} />;
            case 'دسته گل‌های شکلاتی':
                return <Icon theme={config.offeringSchema} as={FaSeedling} />;
            case 'کارگاه‌های آموزشی شکلات‌سازی':
                return <Icon theme={config.offeringSchema} as={FaChalkboardTeacher} />;
            case '1':
                return '1';
            default:
                return <Icon theme={config.offeringSchema} as={FaGift} />; // Default icon if no match
        }
    };

    return (
        <Container theme={config.offeringSchema}
            onMouseEnter={() => setHover(true)}
            onMouseLeave={() => setHover(false)}
        >
            {renderIcon()}
            <Title theme={config.offeringSchema}>{title}</Title>
            <Description theme={config.offeringSchema}>{content}</Description>
            <StyledLink to={goto} theme={config.offeringSchema}>بیشتر بدانید</StyledLink>
        </Container>
    );
};

export default OfferingsSchema;

// Styled Components
const Container = styled.div`
    width: 90%;
    max-width: 300px;
    height: 250px;
    overflow: hidden;
    word-wrap: wrap;
    background-color:  ${({ theme }) => theme.bg};
    border: 2px solid ${({ theme }) => theme.bg};
    border-radius: 20px;
    margin: 20px auto;
    padding: 20px;
    box-shadow: 0 4px 8px ${({ theme }) => theme.bgHover};
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    text-align: center;

    &:hover {
        transform: scale(1.05);
        box-shadow: 0 8px 16px ${({ theme }) => theme.bgHover};
    }

    @media screen and (max-width: 991px) {
        width: 80%;
    }
`;

const Icon = styled.div`
    font-size: 120px;
    color:  ${({ theme }) => theme.icon};
    margin-bottom: 10px;
    transition: color 0.3s ease;
    &:hover {
        color:  ${({ theme }) => theme.iconHaver};
    }
`;

const Title = styled.h1`
    font-size: 22px;
    color: ${({ theme }) => theme.title};
    margin: 10px 0;
`;

const Description = styled.p`
     font-size: 16px;
    color: ${({ theme }) => theme.description};
    margin: 15px 0;
    width: 100%;
    overflow: hidden;
    word-wrap: break-word;
    height: 250px;
    line-height: 1.6;
    padding: 15px;
`;

const StyledLink = styled(Link)`
    font-size: 16px;
    color: ${({ theme }) => theme.styledLink};
    text-decoration: none;
    font-weight: bold;
    margin-top: 15px;
    transition: color 0.3s ease;

    &:hover {
        color: ${({ theme }) => theme.styledLinkHover};
    }
`;
