import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#000000',
        logoContainer: '#1A1A1A',
        h1: '#FFFFFF',
        hamburger: '#FFFFFF',
        hamDiv: '#333333',
        links: '#FFFFFF',
        linksPhone: '#444444',
        linkLi: '#FFFFFF',
        linkA: '#BBBBBB',
        iconContainer: '#1A1A1A',
        iconA: '#FFFFFF',
        profile: '#FFFFFF',
        pulse: '#BBBBBB',
        cart: '#FFFFFF',
    },
    landing: {
        bg: '#121212',
        left: '#1A1A1A',
        textLeft: '#FFFFFF',
        textLeftSpan: '#BBBBBB',
        center: '#FFFFFF',
        shopLink: '#444444',
        centerButtons: '#FFFFFF',
        centerButtonsLink: '#000000',
        centerButton2: '#1A1A1A',
        centerButton2Link: '#FFFFFF',
        storeButton: '#444444',
        aboutButton: '#FFFFFF',
        right: '#1A1A1A',
        textRight: '#FFFFFF',
        textRightSpan: '#BBBBBB',
    },
    offeringSchema: {
        bg: 'rgba(0, 0, 0, 0.8)',
        bgHover: 'rgba(0, 0, 0, 0.9)',
        icon: '#FFFFFF',
        iconHover: '#BBBBBB',
        title: '#FFFFFF',
        description: '#AAAAAA',
        styledLink: '#FFFFFF',
        styledLinkHover: '#BBBBBB',
    },
    offering: {
        bg: 'rgba(0, 0, 0, 0.6)',
        introduction: 'rgba(0, 0, 0, 0.7)',
        introH1: '#FFFFFF',
        introH2: '#CCCCCC',
        introP: '#AAAAAA',
        offers: 'rgba(255, 255, 255, 0.1)',
    },
    contact: {
        bg: 'rgba(0, 0, 0, 0.8)',
        title: '#FFFFFF',
        description: '#CCCCCC',
        socialLinks: '#FFFFFF',
        socialLink: '#BBBBBB',
    },
    clientCounter: {
        bg: 'rgba(0, 0, 0, 0.8)',
        countWrapper: 'rgba(0, 0, 0, 0.7)',
        number: '#FFFFFF',
        counter: '#CCCCCC',
    },
    about: {
        bg: '#121212',
        aboutSection: '#1A1A1A',
        aboutSectionH1: '#FFFFFF',
        aboutSectionH2: '#CCCCCC',
        aboutSectionP: '#AAAAAA',
        faq: '#333333',
        dropdown: '#444444',
        dropdownH1: '#FFFFFF',
        answer: '#222222',
        answerP: '#CCCCCC',
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
