import React, { createContext, useReducer } from 'react';

const initialState = {
    shippingData: {
        firstName: null,
        lastName: null,
        area: null,
        neighbourhood: null,
        street: null,
        alleys: null,
        postalCode: null,
        phoneNumber: null,
        plaque: null,
        deliveryfee: null,
        selectedDate: null,
        selectedTimeSlot: null,
        productQuantityInCart: null,
        totalCartPrice: null,
        totalPrice: null,
        products: [], // array of objs
    }
}

const reduce = (state, action) => {
    switch (action.type) {

        case 'HANDEL_DETAILS':
            return {
                ...state,
                shippingData: {
                    firstName: address.firstName,
                    lastName: address.lastName,
                    area: address.area,
                    neighbourhood: address.neighbourhood,
                    street: address.street,
                    alleys: address.alleys,
                    postalCode: address.postalCode,
                    phoneNumber: address.phoneNumber,
                    plaque: address.plaque,
                    deliveryfee,
                    selectedDate,
                    selectedTimeSlot,
                    productQuantityInCart,
                    totalCartPrice,
                    totalPrice: deliveryfee + totalCartPrice,
                    products,
                }
            };


        default:
            return state
    }
}


export const ShippingContext = createContext();
const ShippingContextProvider = ({ children }) => {

    const [shippingInfo, dispatchShipping] = useReducer(reduce, initialState)

    return (
        <ShippingContext.Provider value={{ shippingInfo, dispatchShipping }}>
            {children}
        </ShippingContext.Provider>
    );
};

export default ShippingContextProvider;
