import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#FFFFFF',
        logoContainer: '#FF5733', // Vibrant orange-red for energy and creativity
        h1: '#C70039', // Bold magenta for expressive text
        hamburger: '#900C3F', // Deep purple for individuality
        hamDiv: '#FFC300', // Bright yellow for a lively touch
        links: '#DAF7A6', // Fresh green for harmony
        linksPhone: '#FF5733', 
        linkLi: '#FFC300',
        linkA: '#900C3F',
        iconContainer: '#FF5733',
        iconA: '#FFC300',
        profile: '#C70039',
        pulse: '#FF5733',
        cart: '#DAF7A6',
    },
    landing: {
        bg: '#FFFFFF',
        left: '#FFC300', // Sunny yellow for inspiration
        textLeft: '#FFFFFF',
        textLeftSpan: '#900C3F',
        center: '#C70039', // Passionate magenta
        shopLink: '#DAF7A6',
        centerButtons: '#FF5733',
        centerButtonsLink: '#FFFFFF',
        centerButton2: '#900C3F',
        centerButton2Link: '#FFFFFF',
        storeButton: '#C70039',
        aboutButton: '#FF5733',
        right: '#DAF7A6',
        textRight: '#FFFFFF',
        textRightSpan: '#C70039',
    },
    offeringSchema: {
        bg: 'rgba(255, 255, 255, 0.9)',
        bgHover: 'rgba(255, 255, 255, 1)',
        icon: '#FFC300',
        iconHover: '#FF5733',
        title: '#C70039',
        description: '#900C3F',
        styledLink: '#DAF7A6',
        styledLinkHover: '#FFC300',
    },
    offering: {
        bg: 'rgba(255, 255, 255, 0.8)',
        introduction: 'rgba(255, 255, 255, 0.7)',
        introH1: '#FF5733',
        introH2: '#900C3F',
        introP: '#546E7A',
        offers: 'rgba(255, 220, 220, 0.03)',
    },
    contact: {
        bg: '#FFFFFF',
        title: '#C70039',
        description: '#900C3F',
        socialLinks: '#FFC300',
        socialLink: '#FF5733',
    },
    clientCounter: {
        bg: 'rgba(255, 255, 255, 0.9)',
        countWrapper: 'rgba(255, 255, 255, 0.8)',
        number: '#DAF7A6',
        counter: '#FFC300',
    },
    about: {
        bg: '#FFFFFF',
        aboutSection: '#FFC300',
        aboutSectionH1: '#C70039',
        aboutSectionH2: '#900C3F',
        aboutSectionP: '#546E7A',
        faq: '#DAF7A6',
        dropdown: '#FF5733',
        dropdownH1: '#C70039',
        answer: '#FFC300',
        answerP: '#900C3F',
    },
    footer: {
        bg: '#FFFFFF',
        footWrapper: '#FFFFFF',
        section: '#900C3F',
        companyTitile: '#C70039',
        companyDescription: '#546E7A',
        sectionTitile: '#FF5733',
        styledLink: '#FFC300',
        finalSection: '#DAF7A6',
        finalText: '#C70039',
        finalLink: '#900C3F',
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
