import React, { useContext } from 'react';
import styled from 'styled-components';
import OfferingsSchema from './OfferSchema';
import { colorContext } from '../../Context/ColorContextProvider';
import { InView } from '@researchgate/react-intersection-observer';  // Importing IntersectionObserver

const data = {
    customChocolates: {
        title: 'شکلات‌های سفارشی',
        content: 'شکلات‌های دست‌ساز با طعم و طراحی منحصربه‌فرد که به سلیقه شما ساخته می‌شوند. از شکلات‌های کلاسیک تا طعم‌های خاص، در ویولا همه چیز به دلخواه شما آماده است.',
        path: '/store'
    },
    // Other data remains the same...
};

const description = '>در ویولا، شکلات‌های سفارشی و منحصربه‌فرد به سلیقه شما طراحی می‌شود. با شکلات‌های دست‌ساز ما هر لحظه را خاص کنید.';

const OffersComponent = () => {
    const { config } = useContext(colorContext);

    return (
        <Container theme={config}>
            <Introduction theme={config}>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>{description}</p>
            </Introduction>

            {/* Wrapping Offers component with InView to trigger animation when it becomes visible */}
            <InView
                as="div"
                onChange={(inView) => {
                    // Add or remove the animation class based on visibility
                    const offersElement = document.getElementById('offers');
                    if (inView) {
                        offersElement.classList.add('slide-in');
                    } else {
                        offersElement.classList.remove('slide-in');
                    }
                }}
                triggerOnce
            >
                <Offers id="offers" theme={config}>
                    <OfferingsSchema
                        title={data.customChocolates.title}
                        content={data.customChocolates.content}
                        goto={data.customChocolates.path}
                    />
                    {/* Other OfferingsSchema components */}
                </Offers>
            </InView>
        </Container>
    );
};

export default OffersComponent;

export const Container = styled.div`
    width: 100%;
    height: fit-content;
    background-color: ${({ theme }) => theme.mainBg};
    padding-top: 100px;
    padding-bottom: 70px;
    overflow: hidden;

    @media screen and (max-width: 991px) {
        margin-top: 50px;
    }
`;

export const Introduction = styled.div`
    width: 100%;
    height: fit-content;
    margin-bottom: 100px;
    text-align: right;

    h1 {
        padding: 30px 80px 0px 0px;
        font-size: 45px;
        color: ${({ theme }) => theme.titile1};
    }

    h2 {
        padding: 20px 80px 10px 0px;
        font-size: 28px;
        color: ${({ theme }) => theme.titile2};
    }

    p {
        padding: 0px 80px 10px 0px;
        font-size: 17px;
        color: ${({ theme }) => theme.description};
    }

    @media screen and (max-width: 991px) {
        margin-bottom: 0px;

        h1 {
            padding: 10px 30px 0px 0px ;
            font-size: 32px;
        }

        h2 {
            padding: 10px 30px 0px 0px ;
            font-size: 22px;
        }

        p {
            padding: 10px 30px 0px 0px ;
            font-size: 16px;
            word-wrap: wrap;
            width: 80%;
            margin-left: auto;
        }
    }
`;

export const Offers = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 18px;
    transition: 1.5s ease;
    margin-top: 40px;
    background-color: ${({ theme }) => theme.mainBg};

    @media screen and (min-width: 991px) {
        width: 90%;
        margin: auto;
        flex-wrap: wrap;
        flex-direction: row;
        justify-content: space-between;
    }
    @media screen and (max-width: 375px) {
        gap: 10px;
    }
`;

// Adding the sliding effect to the Offers component
const slideInAnimation = `
    @keyframes slideIn {
        0% {
            transform: translateX(-100%);
            opacity: 0;
        }
        100% {
            transform: translateX(0);
            opacity: 1;
        }
    }

    .slide-in {
        animation: slideIn 1s ease-out forwards;
    }
`;

// Injecting the animation into the global styles
export const GlobalStyle = createGlobalStyle`
    ${slideInAnimation}
`;
