import React, { useContext } from 'react';
import { colorContext } from '../../Context/ColorContextProvider';
import styled from 'styled-components';
import { Link } from 'react-router-dom';


const ServicesCard = ({ CardData }) => {

    const { config } = useContext(colorContext);
    const { IconImg, title, goTo } = CardData;

    return (
        <Card theme={config}>
            <ImgContainer>
                <img src={IconImg} />
            </ImgContainer>


            <TitleContainer theme={config}>
                {title}
            </TitleContainer>


            <LInkContainer theme={config}>
                <Link to={`${goTo}`}> </Link>
            </LInkContainer>

        </Card>
    );
};

export default ServicesCard;

// Styled Components    
const Card = styled.div` 
    display: flex;
    justify-content: center;
    flex-direction: column;
    align-items: center;
    text-align: center;
    background-color: lightblue;
    width: 95%;
    max-height:250px;
    padding: 10px;
    margin: 10px;
`;

const ImgContainer = styled.div`
    margin: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 95%;
`;

const LInkContainer = styled.div`
    margin: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 95%;
`;


const TitleContainer = styled.div`
    margin: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 95%;
`;
