        bg: '#fffff', // Soft, light cyan background for a clean, refreshing look
        logoContainer: '#004D40', // Dark teal for a sophisticated, calming contrast
        h1: '#FF7043', // Deep teal for headings to tie in with the overall theme
        hamburger: '#004D40', // Dark teal for the hamburger icon to maintain consistency
        hamDiv: '#B2DFDB', // Light teal for the hamburger menu background for a soft look
        links: '#004D40', // Dark teal for links to match the header
        linksPhone: '#B2DFDB', // Light background for mobile links, keeping a fresh feel
        linkLi: '#00796B', // Deep teal for list items to create a cohesive theme
        linkA: '#00796B', // Medium teal for links, offering a smooth contrast
        iconContainer: '#004D40', // Dark teal for icon containers
        iconA: '#FF7043', // Accent teal for icons, aligning with link colors
