        pulse: '#FF7043', // Vibrant coral for hover effects, adding a warm contrast
        cart: '#00796B', // Teal for cart icon to keep uniformity
    },
    landing: {
        bg: '#E1F5FE', // Light blue background for the landing page, creating an airy, inviting feel
        left: '#004D40', // Dark teal background for the left section for contrast
        textLeft: '#FFFFFF', // White text for the left section for readability
        textLeftSpan: '#FF7043', // Coral accent text for emphasis
        center: '#FF7043', // White for the center area for a clean and minimalistic look
        shopLink: '#00796B', // Deep teal for the "Shop Now" link
        centerButtons: '#FF7043', // Coral for attention-grabbing buttons
        centerButtonsLink: '#ffffff', // White text on the buttons for contrast
        centerButton2: '#004D40', // Dark teal for secondary buttons for balance
        centerButton2Link: '#ffffff', // White text for better visibility
        storeButton: '#00796B', // Medium teal for the store button for consistency
        aboutButton: '#FF7043', // Coral for the about button to keep the vibrant theme
        right: '#00796B', // Teal for the right section to create harmony
        textRight: '#FFFFFF', // White text on the right for contrast
        textRightSpan: '#FF7043', // Coral for accents in the right section to add a lively touch
    },
    offeringSchema: {
        bg: ''
    },
    offering: {

    },

};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
