import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#ffffff',
        logoContainer: '#004D40',
        h1: '#FF7043',
        hamburger: '#004D40',
        hamDiv: '#B2DFDB',
        links: '#004D40',
        linksPhone: '#B2DFDB',
        linkLi: '#00796B',
        linkA: '#00796B',
        iconContainer: '#004D40',
        iconA: '#FF7043',
        profile: '#FF7043',
        pulse: '#FF7043',
        cart: '#00796B',
    },
    landing: {
        bg: '#E1F5FE',
        left: '#004D40',
        textLeft: '#FFFFFF',
        textLeftSpan: '#FF7043',
        center: '#FF7043',
        shopLink: '#00796B',
        centerButtons: '#FF7043',
        centerButtonsLink: '#ffffff',
        centerButton2: '#004D40',
        centerButton2Link: '#ffffff',
        storeButton: '#00796B',
        aboutButton: '#FF7043',
        right: '#00796B',
        textRight: '#FFFFFF',
        textRightSpan: '#FF7043',
    },
    offeringSchema: {
        bg: 'rgba(255, 255, 255, 0.8)',
        bgHover: 'rgba(255, 255, 255, 0.9)',
        icon: '#FF7043',
        iconHover: '#FF5722',
        title: '#212121',
        description: '#424242',
        styledLink: '#FF7043',
        styledLinkHover: '#BF360C',
    },
    offering: {
        bg: 'rgba(255, 255, 255, 0.5)',
        introduction: 'rgba(255, 255, 255, 0.5)',
        introH1: '#FF7043',
        introH2: '#37474F',
        introP: '#546E7A',
        offers: 'rgba(0, 0, 0, 0.03)',
    },
    contact: {
        bg: 'rgba(255, 255, 255, 0.8)',
        title: '#00796B',
        description: '#546E7A',
        socialLinks: '#FF7043',
        socialLink: '#FF5722',
    },
    clientCounter:{
        bg:'rgba(255, 255, 255, 0.8)',
        countWrapper:'rgba(255, 255, 255, 0.8)',
        number: '#FF5722',
        counter: '#00796B',
    },
    about: {
        bg: '#E1F5FE', // Same as the landing page background
        aboutSection: '#FFFFFF', // Consistent white container
        aboutSectionH1: '#FF7043', // Main heading in vibrant orange
        aboutSectionH2: '#00796B', // Subheadings in teal
        aboutSectionP: '#546E7A', // Text in muted gray-blue
        faq: '#B2DFDB', // Light teal background for FAQ section
        dropdown: '#FF7043', // Vibrant orange for dropdown
        dropdownH1: '#00796B', // Teal for dropdown headings
        answer: '#E0F7FA', // Soft teal for FAQ answers
        answerP: '#37474F', // Text for FAQ answers in dark blue-gray
    },
    footer: {
        bg:'',
        footWrapper: '',
        section: '',
        companyTitile: '',
        companyDescription: '',
        sectionTitile: '',
        styledLink: '',
        finalSection: '',
        finalText: ''
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
