import React, { useContext } from 'react';
import { Container, Introduction, Offers } from '../Offers/Offers';
import { colorContext } from '../../Context/ColorContextProvider';
import OfferingsSchema from '../Offers/OfferSchema';

const WhyUs = () => {
    const { config } = useContext(colorContext);
    const data = {

        1: {
            title: 'شکلات‌های سفارشی',
            content: 'ما انواع شکلات‌های سفارشی و دست‌ساز را مطابق با سلیقه شما تولید می‌کنیم. از طعم‌های کلاسیک تا طرح‌های منحصر به فرد، هر چیزی که شما بخواهید. این شکلات‌ها برای مناسبت‌های مختلف یا به عنوان هدیه‌ای خاص تهیه می‌شوند.',
            path: '/services'
        },
        2: {
            title: 'باکس‌های هدیه شکلاتی',
            content: 'باکس‌های شکلات لوکس و شکیل، مناسب برای مناسبت‌های خاص مانند تولد، سالگردها و جشن‌ها. هر باکس با دقت و زیبایی خاصی طراحی شده تا تجربه‌ای بی‌نظیر برای شما و عزیزانتان به ارمغان آورد.',
            path: '/services'
        },
        3: {
            title: 'شکلات‌های فصلی ویژه',
            content: 'مجموعه‌ای از شکلات‌های ویژه برای تعطیلات و فصول مختلف. این شکلات‌ها با طعم‌های جدید و تزئینات فصلی تهیه می‌شوند و هر فصل را به یادماندنی می‌کنند.',
            path: '/services'
        },
    };
    
    const description = '>مشتریان ما را برای کیفیت برتر، طراحی‌های منحصر‌به‌فرد و تعهد به ارائه بهترین خدمات انتخاب می‌کنند. ما شکلات‌های سفارشی و باکیفیتی را برای شما فراهم می‌کنیم.';
    
    return (
        <Container theme={config}>
            <Introduction theme={config}>
                <h1>چرا ما   </h1>
                <h2>چرا مشتریان ما را انتخاب می‌کنند</h2>
                <p>{description}</p>
            </Introduction>
            <Offers theme={config}>
                <OfferingsSchema
                    title={data[1].title}
                    content={data[1].content}
                    goto={data[1].path}
                />
                <OfferingsSchema
                    title={data[2].title}
                    content={data[2].content}
                    goto={data[2].path}
                />
                <OfferingsSchema
                    title={data[3].title}
                    content={data[3].content}
                    goto={data[3].path}
                />
            </Offers>
        </Container>

    );
};

export default WhyUs;
