import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
// import { UserContext } from '../../Context/UserContextProvider';
import styled, { keyframes } from 'styled-components';
import { colorContext } from '../../Context/ColorContextProvider';



const Login = () => {

    const { config } = useContext(colorContext);

    // const { userInfo, dispatchInfo } = useContext(UserContext);
    const navigate = useNavigate();
    const [data, setData] = useState({
        name: '',
        password: ''
    });
    const [responseMsg, setResponseMsg] = useState('');

    const onchangeHandler = (e) => {
        const { name, value } = e.target;
        setData({
            ...data,
            [name]: value,
        });
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/login', data, {
                withCredentials: true
            })
            if (response.status === 200) {
                // dispatchInfo({ type: 'LOGIN' })
                const timer = setTimeout(() => {
                    navigate(response.data.redirect)
                }, 1000);
                return () => clearTimeout(timer);
            }
        } catch (error) {
            if (error.response) {
                const { status } = error.response;
                const message = error.response.data.message;
                if (status === 400) {
                    setResponseMsg(message || 'تمام فیلدها الزامی است.');
                } else if (status === 401) {
                    setResponseMsg(message || 'نام کاربری یا رمز عبور صحیح نمیباشد');
                } else if (status === 500) {
                    setResponseMsg(message || 'خطای داخلی سرور. لطفاً بعداً دوباره تلاش کنید.');
                } else {
                    setResponseMsg(message || 'خطای غیرمنتظره رخ داد.');
                }
            } else {
                setResponseMsg('خطای شبکه. لطفاً اتصال خود را بررسی کنید.');
            }
        }
    };

    useEffect(() => {
        const checkSession = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/check-session', {
                    withCredentials: true,
                });
                if (response.data.loggedIn && response.data.redirect) {
                    navigate(response.data.redirect);
                }
            } catch (error) {
                console.log(error);
            }
        };
        checkSession();
    }, [navigate]);

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleLogin();
        }
    };

    return (
        <Wrapper theme = {config.login} onKeyPress={handleKeyPress}>
            <Left theme = {config.login}>
                <Circle className="circle1" theme = {config.login} />
                <Circle className="circle2"  theme = {config.login}/>
                <Circle className="circle3" theme = {config.login} />
                <Circle className="circle4" theme = {config.login} />
                <Welcome theme = {config.login}> ! به فروشگاه ما خوش آمدید</Welcome>
                <Description theme = {config.login}> برای خرید شکلات‌های سفارشی خود، وارد حساب کاربری‌تان شوید.</Description>
                <LinkText theme = {config.login}>
                <StyledLink theme={config.register} to="/login">ورود</StyledLink>
                    <Link to="/register">حساب کاربری ندارید؟ ثبت‌نام کنید</Link>
                </LinkText>
            </Left>
            <Right theme = {config.login}>
                <Container theme = {config.login}>
                    <Heading theme = {config.login}>ورود به حساب</Heading>
                    <Input theme = {config.login}
                        type="text"
                        name="name"
                        value={data.name}
                        placeholder="نام کاربری"
                        onChange={onchangeHandler}
                    />
                    <Input theme = {config.login}
                        type="password"
                        name="password"
                        value={data.password}
                        placeholder="رمز عبور"
                        onChange={onchangeHandler}
                    />
                    <RegisterButton theme = {config.login} onClick={handleLogin}>
                        ورود به حساب
                    </RegisterButton>
                    {responseMsg && <ResponseMsg theme = {config.login}>{responseMsg}</ResponseMsg>}
                </Container>
            </Right>
        </Wrapper>
    );
};

export default Login;

// Keyframes for animations
const fadeIn = keyframes`
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
`;

const slideIn = keyframes`
    from {
        transform: translateX(-100%);
    }
    to {
        transform: translateX(0);
    }
`;

const riseUp = keyframes`
    from {
        transform: translateY(20px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
`;

// Styled components
const Wrapper = styled.div`
  display: flex;
  height: 100vh;
  background: ${({ theme }) => theme.bg};
  overflow: hidden;
  padding-top: 100px;
  flex-direction: row;

  @media (max-width: 991px) {
    flex-direction: column;
    height: fit-content;
  }
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: ${({ theme }) => theme.bg};
  padding: 20px;
  text-align: center;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: radial-gradient(circle,  ${({ theme }) => theme.bigCircleGradient} 20%,  ${({ theme }) => theme.bigCircleGradient2} 40%, transparent 50%);
    pointer-events: none;
    z-index: 0;
  }
`;

const Circle = styled.div`
  position: absolute;
  border-radius: 50%;
  background-color: rgba(94, 75, 139, 0.1);
  z-index: 0;

  &.circle1 {
    width: 50px;
    height: 50px;
    top: 10%;
    left: 10%;
    background-color: ${({ theme }) => theme.c1};
  }

  &.circle2 {
    width: 30px;
    height: 30px;
    top: 60%;
    left: 20%;
    background-color: ${({ theme }) => theme.c2};

  }

  &.circle3 {
    width: 40px;
    height: 40px;
    top: 30%;
    right: 15%;
    background-color: ${({ theme }) => theme.c3};

  }

  &.circle4 {
    width: 60px;
    height: 60px;
    bottom: 10%;
    right: 5%;
    background-color: ${({ theme }) => theme.c4};
    
  }
`;


const Welcome = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(255, 255, 255, 0.3);
  animation: ${slideIn} 1s ease-out;
  color: ${({ theme }) => theme.welcome};
  z-index: 100;
`;

const Description = styled.p`
  font-size: 1rem;
  max-width: 600px;
  opacity: 0;
  animation: ${fadeIn} 1s 0.5s forwards;
  text-align: center;
  color: ${({ theme }) => theme.description};
  margin: 10px 0;
  line-height: 1.4;
  border-radius: 10px;
  z-index: 100;
  padding: 10px;
`;

const LinkText = styled.p`
  font-size: 1rem;
  color: ${({ theme }) => theme.linktext};
`;
const StyledLink = styled(Link)`
  color: ${({ theme }) => theme.styledLink};
  text-decoration: underline;
`;

const Right = styled.div`
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
`;

const Container = styled.div`
    background: #ffffff;
    padding: 20px;
    border-radius: 20px;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
    width: 80%;
    max-width: 350px;
    transform: translateY(20px);
    opacity: 0;
    animation: ${riseUp} 1s ease-out forwards;
    text-align: center;
`;

const Heading = styled.h2`
    font-size: 1.8rem;
    margin-bottom: 20px;
    color: #5e4b8b;
    animation: ${fadeIn} 1s forwards;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
`;

const Input = styled.input`
    width: 90%;
    padding: 10px;
    margin: 8px 0;
    border: 2px solid #5e4b8b;
    border-radius: 50px;
    font-size: 14px;
    color: #333;
    background: rgba(255, 255, 255, 0.9);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
    animation: ${fadeIn} 1s forwards;

    &:hover {
