import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#F8F9FA',
        logoContainer: '#5A189A', 
        h1: '#1D3557', 
        hamburger: '#A663CC', 
        hamDiv: '#E9C46A', 
        links: '#457B9D', 
        linksPhone: '#E63946', 
        linkLi: '#A663CC',
        linkA: '#1D3557',
        iconContainer: '#5A189A',
        iconA: '#E63946',
        profile: '#E63946',
        pulse: '#A663CC',
        cart: '#457B9D',
    },
    landing: {
        bg: '#F8F9FA',
        left: '#E9C46A', 
        textLeft: '#FFFFFF',
        textLeftSpan: '#A663CC',
        center: '#5A189A', 
        shopLink: '#457B9D',
        centerButtons: '#E63946',
        centerButtonsLink: '#FFFFFF',
        centerButton2: '#1D3557',
        centerButton2Link: '#FFFFFF',
        storeButton: '#E9C46A',
        aboutButton: '#A663CC',
        right: '#457B9D',
        textRight: '#FFFFFF',
        textRightSpan: '#1D3557',
    },
    offeringSchema: {
        bg: 'rgba(248, 249, 250, 0.9)',
        bgHover: 'rgba(248, 249, 250, 1)',
        icon: '#E63946',
        iconHover: '#E9C46A',
        title: '#1D3557',
        description: '#5A189A',
        styledLink: '#A663CC',
        styledLinkHover: '#457B9D',
    },
    offering: {
        bg: 'rgba(248, 249, 250, 0.8)',
        introduction: 'rgba(248, 249, 250, 0.7)',
        introH1: '#E63946',
        introH2: '#1D3557',
        introP: '#457B9D',
        offers: 'rgba(255, 228, 202, 0.03)',
    },
    contact: {
        bg: '#F8F9FA',
        title: '#1D3557',
        description: '#457B9D',
        socialLinks: '#A663CC',
        socialLink: '#E63946',
    },
    clientCounter: {
        bg: 'rgba(248, 249, 250, 0.9)',
        countWrapper: 'rgba(248, 249, 250, 0.8)',
        number: '#E9C46A',
        counter: '#457B9D',
    },
    about: {
        bg: '#F8F9FA',
        aboutSection: '#E9C46A',
        aboutSectionH1: '#1D3557',
        aboutSectionH2: '#457B9D',
        aboutSectionP: '#5A189A',
        faq: '#A663CC',
        dropdown: '#E63946',
        dropdownH1: '#1D3557',
        answer: '#E9C46A',
        answerP: '#457B9D',
    },
    footer: {
        bg: '#F8F9FA',
        footWrapper: '#F8F9FA',
        section: '#5A189A',
        companyTitile: '#1D3557',
        companyDescription: '#457B9D',
        sectionTitile: '#A663CC',
        styledLink: '#E63946',
        finalSection: '#E9C46A',
        finalText: '#1D3557',
        finalLink: '#5A189A',
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
