import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaUserCircle, FaShoppingCart, FaSignOutAlt, FaClipboardList, FaStore } from 'react-icons/fa';
import axios from 'axios';
import { UserContext } from '../../';
import moment from 'moment-jalaali';
import styled from 'styled-components';

const Customer = () => {
    const { userInfo, dispatchInfo } = useContext(UserContext);
    const [responseMsg, setResponseMsg] = useState('');
    const navigate = useNavigate();
    const [currentTime, setCurrentTime] = useState(new Date());
    const [selectedSection, setSelectedSection] = useState('AccDetail');

    const getUserInfo = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/cdash', { withCredentials: true });
            if (response.status === 200) {
                dispatchInfo({ type: 'SET_DATA', payload: response.data.data });
            }
        } catch (error) {
            setResponseMsg(error.response ? error.response.data.message : 'خطای شبکه');
        }
    };

    const [passwordData, setPasswordData] = useState({
        currentPassword: '',
        newPassword: ''
    });

    const [phoneData, setPhoneData] = useState({
        newPhone: ''
    });

    useEffect(() => {
        const timer = setInterval(() => {
            setCurrentTime(new Date());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const logout = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/logout', {}, { withCredentials: true });
            if (response.status === 200 && response.data.redirect) {
                navigate(response.data.redirect);
            }
        } catch (error) {
            console.error('Error during logout:', error.response ? error.response.data.message : error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPasswordData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handlePasswordChange = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.patch('http://localhost:8080/api/change-password', passwordData, { withCredentials: true });

            if (response.status === 200) {
                setResponseMsg('رمز عبور با موفقیت تغییر کرد');
                getUserInfo();
            }
        } catch (error) {
            setResponseMsg(error.response ? error.response.data.message : 'خطا در تغییر رمز عبور');
        }
        setPasswordData({
            newPassword: '',
            currentPassword: ''
        });
    };

    const handlePhoneChange = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.patch('http://localhost:8080/api/change-phone', phoneData, { withCredentials: true });

            if (response.status === 200) {
                setResponseMsg('شماره تماس با موفقیت تغییر کرد');
                getUserInfo();
            }
        } catch (error) {
            setResponseMsg(error.response ? error.response.data.message : 'خطا در تغییر شماره تماس');
        }
        setPhoneData({
            newPhone: ''
        });
    };

    useEffect(() => {
        if (responseMsg) {
            const timer = setTimeout(() => {
                setResponseMsg(""); // Clear the message
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [responseMsg]);

    useEffect(() => {
        getUserInfo();
    }, []);

    return (
        <CustomerContainer>
            <Sidebar>
                <ul>
                    <li className={`iconWithText ${selectedSection === 'AccDetail' ? 'selected' : ''}`} onClick={() => setSelectedSection('AccDetail')}>
                        <FaUserCircle />
                        <span className="iconText">پروفایل</span>
                    </li>
                    <li className={`iconWithText ${selectedSection === 'Orders' ? 'selected' : ''}`} onClick={() => setSelectedSection('Orders')}>
                        <FaClipboardList />
                        <span className="iconText">سفارشات</span>
                    </li>
                    <Link to="/store" className="iconWithText">
                        <FaStore />
                        <span className="iconText">فروشگاه</span>
                    </Link>
                    <li className="iconWithText out" onClick={logout}>
                        <FaSignOutAlt />
                        <span className="iconText">خروج</span>
                    </li>
                </ul>
            </Sidebar>
            <ContentArea>
                <CurrentSection>
                    <h2>خوش آمدید, {userInfo.username}</h2>
                    <p>{currentTime.toLocaleTimeString()}</p>
                    <p>{responseMsg}</p>
                </CurrentSection>
                {selectedSection === 'AccDetail' && (
                    <ProfileSection>
                        <h3>اطلاعات کاربری</h3>
                        <p><strong>نام کاربری:</strong> {userInfo.username}</p>
                        <p><strong>شماره تماس:</strong> {userInfo.phone}</p>
                        <p>
                            <strong>تاریخ عضویت:    </strong>
                            {moment(userInfo.timeCreated).locale('fa').format('YYYY/MM/DD')}
                        </p>

                        <h3>تغییر رمز عبور</h3>
                        <form onSubmit={handlePasswordChange}>
                            <label>
                                رمز عبور فعلی:
                                <input
                                    type="password"
                                    name="currentPassword"
                                    value={passwordData.currentPassword}
                                    onChange={handleChange}
                                    required
                                />
                            </label>
                            <label>
                                رمز عبور جدید:
                                <input
                                    type="password"
                                    name="newPassword"
                                    value={passwordData.newPassword}
                                    onChange={handleChange}
                                    required
                                />
                            </label>
                            <button type="submit">تغییر رمز عبور</button>
                        </form>

                        <h3>تغییر شماره تماس</h3>
                        <form onSubmit={handlePhoneChange}>
                            <label>
                                شماره تماس جدید:
                                <input
                                    type="text"
                                    name="newPhone"
                                    value={phoneData.newPhone}
                                    onChange={(e) => setPhoneData({ ...phoneData, newPhone: e.target.value })}
                                    required
                                />
                            </label>
                            <button type="submit">تغییر شماره تماس</button>
                        </form>
                    </ProfileSection>
                )}
                {selectedSection === 'Orders' && (
                    <OrdersSection>
                        <h3>سفارشات شما</h3>
                        {/* Replace this with your order list logic */}
                        <p>لیست سفارشات</p>
                    </OrdersSection>
                )}
            </ContentArea>
        </CustomerContainer>
    );
};

const CustomerContainer = styled.div`
    display: flex;
    min-height: 100vh;
    padding: 20px;
    background-color: #f9f9f9;
    font-family: 'Poppins', sans-serif;
    color: #333;
`;

