    padding: 20px;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
    transition: width 0.3s ease-in-out;
    
    ul {
        list-style-type: none;
        padding: 0;
        margin: 0;
    }
    
    .iconWithText {
        display: flex;
        align-items: center;
        gap: 10px;
        cursor: pointer;
        font-size: 16px;
        color: #555;
        transition: all 0.3s ease;

        &:hover {
            color: #4b3d8d;
        }

        .iconText {
            display: none;
        }

        &.selected {
            color: #4b3d8d;
        }
    }

    .out {
        color: #e74c3c;
        &:hover {
            color: #c0392b;
        }
    }

    .iconWithText.selected .iconText {
        display: inline-block;
    }
`;

const ContentArea = styled.div`
