import React, { useContext, useState } from 'react';
import { FaShoppingCart, FaUserCircle } from 'react-icons/fa';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
// import { ProductContext } from '../../Context/ProductContextProvider';

const Nav = () => {
    const [open, setOpen] = useState(false);
    // const { state } = useContext(ProductContext);
    const location = useLocation(); // Get the current location

    // Check if we are on the login or register route
    const isDarkTheme = location.pathname === '/login' || location.pathname === '/register';

    return (
        <Container isDarkTheme={isDarkTheme}>
            {/* Hamburger menu */}
            <Hamburger open={open} onClick={() => setOpen(!open)}>
                <div></div>
                <div></div>
                <div></div>
            </Hamburger>
            {/* Logo */}
            <Logo>
                <h1>ویولا</h1>
            </Logo>
            {/* Links */}
            <Links open={open}>
                <li><Link to="/store">محصولات</Link></li>
                <li><Link to="/contact">تماس با ما</Link></li>
                <li><Link to="/about">درباره ما</Link></li>
                <li><Link to="/">صفحه اصلی</Link></li>
            </Links>
            {/* Icon container */}
            <IconContainer>
                <Link to="/dashboard">
                    <FaUserCircle className="profile" />
                </Link>
                <Link to="/cart">
                    <FaShoppingCart className={'icon'} />
                </Link>
            </IconContainer>
        </Container>
    );
};

export default Nav;

// Styled Components

const Container = styled.div`
    max-height: 80px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    padding: 20px;
    border-radius: 16px;
    margin: 20px auto;
    background: rgba(142, 68, 173, 0.02);
    box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
    backdrop-filter: blur(0px);
    -webkit-backdrop-filter: blur(0px);
    ${props => props.isDarkTheme && `
        background-color: #1a1a1a;
        color: white;
        border-radius: 0px;
        margin: 0px;
    `}
`;

const Hamburger = styled.div`
    width: 2rem;
    height: 2rem;
    position: fixed;
    top: 15px;
    left: 10px;
    z-index: 20;
    display: none;
    cursor: pointer;
    
    div {
        width: 2rem;
        height: 0.25rem;
        background: linear-gradient(135deg, #A68EBD, #8E44AD);
        border-radius: 10px;
        transform-origin: 1px;
        transition: all 0.3s linear;
    }

    ${props => props.open && `
        div:nth-child(1) {
            transform: rotate(45deg);
        }
        div:nth-child(2) {
            transform: translateX(-500%);
            opacity: 0;
        }
        div:nth-child(3) {
            transform: rotate(-45deg);
        }
    `}

    @media (max-width: 899px) {
        display: flex;
        flex-direction: column;
        justify-content: space-around;
    }
`;

const Logo = styled.div`
    color: #8E44AD;

    h1 {
        font-size: 24px;
        font-weight: bold;
    }
            @media (max-width: 899px) {
        display: flex;
        flex-direction: column;
        justify-content: space-around;
    }
`;

const Links = styled.ul`
    list-style: none;
    display: flex;
    gap: 30px;
    padding: 12px 10px;

    li {
        padding: 5px 0;
    }

    li a {
        text-decoration: none;
        color: black;
        transition: color 0.3s ease;
        font-size: 18px;
        white-space: nowrap;
        position: relative;
    }

    li a::after {
        content: "";
        position: absolute;
        left: 50%;
        bottom: -3px;
        transform: translateX(-50%);
        width: 0;
        height: 2px;
        background-color: #8E44AD;
        transition: width 0.3s ease;
    }

    li a:hover::after {
        width: 100%;
    }

    li a:hover {
        color: #8E44AD;
    }

    ${props => props.open && `
        flex-direction: column;
        position: fixed;
        top: 0;
        right: 0;
        width: 250px;
        height: 100vh;
        border-radius: 30px 0 0 30px;
        transform: translateX(100%);
        transition: transform 0.3s ease;
        padding-top: 80px;
        backdrop-filter: blur(12px);
        background: rgba(166, 142, 189, 0.85);
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
    `}

    @media (max-width: 899px) {
        ${props => props.open && `
            transform: translateX(0);
            z-index: 1000;
        `}

        li {
            text-align: center;
            padding: 15px;
            cursor: pointer;
            border-radius: 5px;
            margin-bottom: 10px;
        }

        li:hover {
            background-color: rgba(255, 255, 255, 0.1);
        }

        li a {
            color: #fff;
            font-size: 20px;
        }
    }
`;

const IconContainer = styled.div`
    display: flex;
    justify-content: center;
    flex-direction: row;
    align-items: center;

    .icon {
        font-size: 30px;
        transition: color 0.3s ease;
        margin: 10px;
        color: rgb(150, 149, 149);
    }

    .ItemsSelected {
        font-size: 30px;
        margin: 10px;
        transition: color 0.3s ease;
        animation:  infinite 1s ease-in-out;
    }

    .profile {
        transition: color 0.3s ease;
        font-size: 30px;
        color: rgb(121, 79, 158);
        margin: 10px;
    }

    .profile:hover {
        color: lightgrey;
        animation:  0.9s ease-in-out infinite;
    }

    .icon:hover {
        color: rgb(121, 79, 158);
        animation:  0.4s ease-in-out;
    }
`;
