import React from 'react';
import { Link } from 'react-router-dom';
import { FaInstagram, FaTelegram, FaYoutube, FaTwitter } from 'react-icons/fa';
import styled from 'styled-components';

const Container = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;
    background: linear-gradient(135deg, #A68EBD, #8E44AD);
    padding-top: 40px;
`;

const FooterWrapper = styled.div`
    display: flex;
    justify-content: space-between;  /* Spread out sections evenly */
    align-items: center;  /* Align all sections' items at the same height */
    width: 100%;
    padding-bottom: 20px;

    @media screen and (max-width: 991px) {
        flex-direction: column;
        align-items: center;  /* For mobile, items will align vertically centered */
    }
`;

const Section = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;  /* Align the items in each section to the center */
    padding: 10px;
    text-align: center;
`;

const CompanyTitle = styled.h1`
    color: #8E44AD;
    font-size: 35px;
    font-weight: bold;
    padding: 10px;
`;

const CompanyDescription = styled.p`
    line-height: 30px;
    font-size: 14px;
    color: #bcbcbc;
`;

const SectionTitle = styled.h2`
    color: #e4e6e4;
    font-weight: 900;
    font-size: 20px;
`;

const StyledLink = styled(Link)`
    color: #DDDDDD;
    text-decoration: none;
    padding: 8px;
    font-family: sans-serif;
    font-weight: 300;
    font-size: 14px;
    transition: all 0.1s ease-in;

    &:hover {
        color: #2b083a;
        font-size: 16px;
    }
`;

const FinalSection = styled.div`
    width: 100%;
    height: fit-content;
    border-top: #bcbcbc4f solid 1px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 15px;
    flex-direction: column;

    @media screen and (min-width: 991px) {
        flex-direction: row;
        opacity: 0.4;
        transition: all 0.3s ease-in-out;

        &:hover {
            opacity: 1;
        }
    }
`;

const FinalText = styled.p`
    padding: 15px;
    color: #fff;
    font-weight: bold;
`;

const HighlightSpan = styled.span`
    color: gold;
    cursor: pointer;
`;

const FinalLink = styled(Link)`
    padding: 15px;
    font-weight: bold;
    text-decoration: none;
    color: lightskyblue;
`;

const Footer = () => {
    return (
        <Container>
            <FooterWrapper>
                <Section>
                    <CompanyTitle>ویولا</CompanyTitle>
                    <CompanyDescription>
                        در شکلات خوشمزه بهترین شکلات‌های دست‌ساز را تجربه کنید. ما شکلات‌های سفارشی برای تمام مناسبت‌ها با طعم‌ها و طراحی‌های منحصر به فرد ارائه می‌دهیم.
                    </CompanyDescription>
                </Section>

                <Section>
                    <SectionTitle>ناوبری سایت</SectionTitle>
                    <StyledLink to="/">خانه</StyledLink>
                    <StyledLink to="/products">شکلات‌های ما</StyledLink>
                    <StyledLink to="/about">درباره ما</StyledLink>
                    <StyledLink to="/contact">تماس با ما</StyledLink>
                </Section>

                <Section>
                    <SectionTitle>ما را دنبال کنید</SectionTitle>
                    <StyledLink to="https://www.instagram.com" target="_blank">
                        <FaInstagram size={20} />
                    </StyledLink>
                    <StyledLink to="https://t.me" target="_blank">
                        <FaTelegram size={20} />
                    </StyledLink>
                    <StyledLink to="https://www.youtube.com" target="_blank">
                        <FaYoutube size={20} />
                    </StyledLink>
                    <StyledLink to="https://twitter.com" target="_blank">
                        <FaTwitter size={20} />
                    </StyledLink>
                </Section>

                <Section>
                    <SectionTitle>خدمات ما</SectionTitle>
                    <StyledLink to="/custom-orders">سفارشات سفارشی</StyledLink>
                    <StyledLink to="/gifts">هدایای شکلاتی</StyledLink>
                    <StyledLink to="/subscriptions">برنامه‌های اشتراک</StyledLink>
                    <StyledLink to="/corporate">هدایای شرکتی</StyledLink>
                </Section>
            </FooterWrapper>

            <FinalSection>
                <FinalText>
                    &copy; 2024 طراحی شده توسط: <HighlightSpan>شکلات خوشمزه</HighlightSpan>
                </FinalText>
                <FinalLink to="/terms">شرایط و سیاست حفظ حریم خصوصی</FinalLink>
            </FinalSection>
        </Container>
    );
};

export default Footer;
