import React, { useState } from 'react';
import styled from 'styled-components';
import { Introduction } from '../Offers/Offers';

const Container = styled.div`
  width: 100%;
  height: fit-content;
  background-color: #ffffff;
  padding-top: 50px;
  text-align: right;
  overflow: hidden;
  padding-bottom: 70px;
  margin-top: 100px;
  display: flex;
  flex-direction: row;


  @media screen and (max-width: 991px) {
    padding-top: 20px;
    margin-top: 50px;
    display: flex;
   flex-direction: column;

  }
`;

const AboutSection = styled.div`
  width: 100%;
  height: fit-content;

  h1 {
    font-size: 14px;
    color: #8e44ad;
    padding: 20px 0px 0px 20px;
    line-height: 0px;

    @media screen and (max-width: 375px) {
      font-size: 12px;
      padding: 5px 0px 0px 10px;
    }
  }

  h2 {
    font-size: 38px;
    color: #401e4f;
    padding: 20px 0px 0px 20px;
    line-height: 50px;

    @media screen and (max-width: 375px) {
      font-size: 24px;
      padding: 5px 0px 0px 10px;
    }
  }

  p {
    padding: 25px 30px 50px 20px;
    font-size: 17px;
    color: #777;
    line-height: 35px;

    @media screen and (max-width: 375px) {
      padding: 10px;
      font-size: 13px;
      line-height: 1.8;
    }
  }
`;

const FAQSection = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 18px;
  transition: 1.5s ease;

  @media screen and (min-width: 991px) {
    flex-wrap: wrap;
    flex-direction: row;
  }

  @media screen and (max-width: 375px) {
    gap: 10px;
  }
`;

const Dropdown = styled.div`
  width: 90%;
  height: fit-content;
  background-color: #f8f9fc;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 9px;
  transition: all 0.1s ease-in;
  cursor: pointer;

  h1 {
    color: #401e4f;
    font-size: 15px;
    padding: 30px;
    line-height: 25px;
    text-align: center;
    margin: auto;
    transition: all 0.3s ease-in;
    display: block;
    width: 100%;

    &:hover {
      color: #8e44ad;
    }

    @media screen and (max-width: 375px) {
      font-size: 12px;
      padding: 15px;
    }
  }

  @media screen and (min-width: 991px) {
    margin: auto;
  }

  @media screen and (max-width: 375px) {
    margin-top: 30px;
    width: 100%;
    padding: 8px;
  }
`;

const Answer = styled.div`
  max-height: ${({ show }) => (show ? '500px' : '0')};
  opacity: ${({ show }) => (show ? '1' : '0')};
  overflow: hidden;
  transition: max-height 0.6s ease-in-out, padding 0.6s ease-in-out, opacity 0.3s ease-in-out;
  padding: ${({ show }) => (show ? '10px' : '0 10px')};

  p {
    color: #777;
    text-align: center;
    line-height: 25px;
  }
`;

const About = () => {

    const [show, setShow] = useState(false);
    const [show2, setShow2] = useState(false);
    const [show3, setShow3] = useState(false);
    const [show4, setShow4] = useState(false);

    return (
        <Container>
            {/* <AboutSection>
        <h1>درباره ما / سوالات متداول</h1>
        <h2>
          شکلات‌های دست‌ساز، سالم و سفارشی با <span>"نام فروشگاه"</span>
        </h2>
        <p>
          در فروشگاه آنلاین شکلات ما، شما می‌توانید شکلات‌های دست‌ساز و سالم با طعم‌های مختلف و با
          استفاده از بهترین مواد اولیه پیدا کنید. ما به تولید شکلات‌هایی با کیفیت بالا و طعمی منحصر به
          فرد اعتقاد داریم. تمامی محصولات ما ارگانیک و بدون مواد افزودنی مضر هستند تا شما تجربه‌ای
          خوشمزه و سالم را تجربه کنید. علاوه بر این، شما می‌توانید شکلات‌ها را به شکل بسته‌بندی‌های
          هدیه سفارشی، مناسب برای مناسبت‌های خاص مانند تولدها، اعیاد و جشن‌ها سفارش دهید.
        </p>
      </AboutSection> */}

            <Introduction theme={config.offering}>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>در [نام فروشگاه شما]، ما تخصصی در تولید شکلات‌های سفارشی و هدیه‌های خاص داریم.<br />با ما طعم و ظرافت را تجربه کنید.</p>
            </Introduction>

            <FAQSection>
                <Dropdown onClick={() => setShow(!show)}>
                    <h1>آیا شکلات‌های شما سالم و ارگانیک هستند؟</h1>
                    <Answer show={show}>
                        <p>
                            بله، تمامی شکلات‌های ما با استفاده از مواد ارگانیک و طبیعی تولید می‌شوند. ما به سلامت شما
                            اهمیت می‌دهیم و از هیچ‌گونه مواد شیمیایی یا افزودنی‌های مضر استفاده نمی‌کنیم.
                        </p>
                    </Answer>
                </Dropdown>

                <Dropdown onClick={() => setShow2(!show2)}>
                    <h1>آیا می‌توانم شکلات‌ها را به صورت بسته‌بندی‌های هدیه سفارش دهم؟</h1>
                    <Answer show={show2}>
                        <p>
                            بله، شما می‌توانید شکلات‌ها را در بسته‌بندی‌های هدیه سفارشی دریافت کنید. ما انواع
                            بسته‌بندی‌های شیک و جذاب برای مناسبت‌های مختلف مثل تولد، اعیاد و جشن‌ها ارائه می‌دهیم.
                        </p>
                    </Answer>
                </Dropdown>

                <Dropdown onClick={() => setShow3(!show3)}>
                    <h1>آیا شکلات‌های شما برای گیاه‌خواران مناسب هستند؟</h1>
                    <Answer show={show3}>
                        <p>
                            بله، بسیاری از شکلات‌های ما به طور خاص برای گیاه‌خواران تولید شده‌اند. ما به شما این
                            اطمینان را می‌دهیم که می‌توانید با خیال راحت از شکلات‌های ما لذت ببرید، بدون اینکه نگران
                            مواد غیرمجاز باشید.
                        </p>
                    </Answer>
                </Dropdown>

                <Dropdown onClick={() => setShow4(!show4)}>
                    <h1>آیا امکان بازگشت محصولات وجود دارد؟</h1>
                    <Answer show={show4}>
                        <p>
                            بله، در صورتی که مشکلی با سفارش خود داشته باشید، می‌توانید درخواست بازگشت محصول دهید. ما
                            تضمین می‌کنیم که تجربه خریدی رضایت‌بخش را برای شما فراهم کنیم.
                        </p>
                    </Answer>
                </Dropdown>
            </FAQSection>
        </Container>
    );
};

export default About;
