import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Orders = () => {
    const [orders, setOrders] = useState([]); // State to store orders data

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/get-orders', { withCredentials: true });
                if (response.status === 200) {
                    setOrders(response.data); // Set the orders data to state
                }
            } catch (error) {
                console.log(error);
            }
        };
        fetchOrders();
    }, []);

    return (
        <div>
            <h1>Orders</h1>
            {orders.length > 0 ? (
                orders.map((order, index) => (
                    <div key={index} className="order-card">
                        <h2>User: {order.user.firstName} {order.user.lastName}</h2>
                        <p><strong>Phone Number:</strong> {order.user.phoneNumber}</p>
                        <p><strong>Status:</strong> {order.status}</p>
                        <p><strong>Address:</strong> {order.address.area}, {order.address.neighbourhood}, {order.address.street}, {order.address.alleys}, {order.address.postalCode}, {order.address.plaque}</p>
                        <p><strong>Date and Time:</strong> {new Date(order.dateAndTime.date).toLocaleDateString()} at {order.dateAndTime.time}</p>

                        <h3>Ordered Products:</h3>
                        <ul>
                            {order.orderedProducts.map((product, idx) => (
                                <li key={idx}>
                                    <strong>{product.productName}</strong> (Price: {product.productPrice} | Quantity: {product.quantity})
                                </li>
                            ))}
                        </ul>
                    </div>
                ))
            ) : (
                <p>No orders found.</p>
            )}
        </div>
    );
};

export default Orders;
