import React, { useState, useEffect, useContext } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { colorContext } from '../../Context/ColorContextProvider';
import { validateSignUp } from '../../Helper/SignUpValidator';



const Register = () => {

  const { config } = useContext(colorContext);
  const navigate = useNavigate();
  const [data, setData] = useState({ name: '', phone: '', password: '' });
  const [responseMsg, setResponseMsg] = useState('');

  const onchangeHandler = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const registerUser = async () => {
    const validationErrors = validateSignUp(data, 'register');
    if (Object.keys(validationErrors).length !== 0) {
      validateSignUp(data, 'register');
      setTouched({
        name: true,
        password: true,
        phone: true
      })
      return
    }

    try {
      setErrors({})
      setTouched({})
      if (!data.name || !data.phone || !data.password) {
        setResponseMsg('تمام فیلدها الزامی است');
        return;
      }
      const response = await axios.post('http://localhost:8080/api/register', data, {
        withCredentials: true,
      });

      if (response.status === 201) {
        navigate(response.data.redirect);
      }
    } catch (error) {
      if (error.response) {
        const { status } = error.response;
        const message = error.response.data.message;

        if (status === 400) {
          setResponseMsg(message || 'تمام فیلدها الزامی است.');
        } else if (status === 409) {
          setResponseMsg(message || 'نام کاربری یا شماره قبلاً ثبت شده است.');
        } else if (status === 500) {
          setResponseMsg(message || 'خطای داخلی سرور. لطفاً بعداً دوباره تلاش کنید.');
        } else {
          setResponseMsg(message || 'خطای غیرمنتظره رخ داد.');
        }
      } else {
        setResponseMsg('خطای شبکه. لطفاً اتصال خود را بررسی کنید.');
      }
    }
  };

  useEffect(() => {
    const checkSession = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/check-session', {
          withCredentials: true,
        });
        if (response.data.loggedIn && response.data.redirect) {
          navigate(response.data.redirect);
        }
      } catch (error) {
        // Handle errors based on response from server
        if (error.response) {
          const { status } = error.response;
          const message = error.response.data.message || "خطای غیرمنتظره رخ داد.";
          
          switch (status) {
            case 400:
              setResponseMsg(message || 'تمام فیلدها الزامی است.');
              break;
            case 409:
              setResponseMsg(message || 'نام کاربری یا شماره قبلاً ثبت شده است.');
              break;
            case 500:
              setResponseMsg(message || 'خطای داخلی سرور. لطفاً بعداً دوباره تلاش کنید.');
              break;
            case 422:
              setResponseMsg(message || 'داده‌های ورودی معتبر نیستند.');
              break;
            default:
              setResponseMsg(message || 'خطای غیرمنتظره رخ داد.');
              break;
          }
        } else if (error.request) {
          // Network error (e.g., no response received)
          setResponseMsg('خطای شبکه. لطفاً اتصال خود را بررسی کنید.');
        } else {
          // Any other unexpected error
          console.error('Error:', error.message);
          setResponseMsg('خطای غیرمنتظره رخ داد.');
        }
      }
    };
    checkSession();
  }, [navigate]);

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      registerUser();
    }
  };


  const [errors, setErrors] = useState({});

  const [touched, setTouched] = useState({});

  const focusHandler = (e) => {
    setTouched({ ...touched, [e.target.name]: true, })
  }

  useEffect(() => {
    setErrors(validateSignUp(data, 'register'))
  }, [data, touched])


  return (
    <Wrapper theme={config} onKeyPress={handleKeyPress}>
      <Left theme={config}>
        <Circle theme={config} className="circle1" />
        <Circle theme={config} className="circle2" />
        <Circle theme={config} className="circle3" />
        <Circle theme={config} className="circle4" />
        <Welcome theme={config}> ! به فروشگاه ما خوش آمدید</Welcome>
        <Description theme={config}>
          لطفاً برای ادامه ثبت‌نام کنید. این یک فروشگاه آنلاین است که می‌توانید شکلات‌های سفارشی و همچنین بسته‌هایی برای هر مناسبت بخرید.
        </Description>

      </Left>
      <Right theme={config}>
        <Container theme={config}>
          <Heading theme={config}> ثبت نام</Heading>


          <Input
            theme={config}
            name="name"
            value={data.name}
            onChange={onchangeHandler}
            placeholder="نام"
            onFocus={focusHandler}
          />
          <span
            style={{
              display: 'block', // Ensures the span takes up space
              minHeight: '1rem', // Set a fixed height to reserve space
              color: 'red',
              opacity: touched.name ? '1' : '0',
              transition: 'opacity 0.3s ease', // Smooth fade-in effect
            }}
          >
            {errors.name && touched.name && `${errors.name}`}
          </span>

          <Input
            theme={config}
            name="phone"
            value={data.phone}
            onChange={onchangeHandler}
            placeholder="شماره تلفن"
            onFocus={focusHandler}
          />
          <span
            style={{
              display: 'block',
              minHeight: '1rem',
              color: 'red',
              opacity: touched.phone ? '1' : '0',
              transition: 'opacity 0.3s ease',
            }}
          >
            {errors.phone && touched.phone && `${errors.phone}`}
          </span>

          <Input
            theme={config}
            type="password"
            name="password"
            value={data.password}
            onChange={onchangeHandler}
            placeholder="رمز عبور"
            onFocus={focusHandler}
          />
          <span
            style={{
              display: 'block',
              minHeight: '1rem',
              color: 'red',
              opacity: touched.password ? '1' : '0',
              transition: 'opacity 0.3s ease',
            }}
          >
            {errors.password && touched.password && `${errors.password}`}
          </span>

          <RegisterButton theme={config} onClick={registerUser}>ثبت‌نام</RegisterButton>

          <LinkText theme={config}>
            قبلاً حساب دارید؟ <StyledLink theme={config} to="/login">ورود</StyledLink>
          </LinkText>
          {responseMsg && <ResponseMsg theme={config}> {responseMsg}</ResponseMsg>}
        </Container>
      </Right>
    </Wrapper>
  );
};

export default Register;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const slideIn = keyframes`
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
`;

const riseUp = keyframes`
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
`;

