import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#FFFFFF', // Main white background
        logoContainer: '#FF4081', // Bright pink
        h1: '#3D5AFE', // Vivid blue
        hamburger: '#1DE9B6', // Neon teal
        hamDiv: '#FFD740', // Vibrant yellow
        links: '#651FFF', // Electric purple
        linksPhone: '#FFAB00', // Amber
        linkLi: '#00E676', // Neon green
        linkA: '#00B0FF', // Bright sky blue
        iconContainer: '#FF4081', // Matching bright pink
        iconA: '#FF4081', // Vibrant accent pink
        profile: '#FFD740', // Matching yellow for highlight
        pulse: '#FF4081', // Pink pulse
        cart: '#3D5AFE', // Consistent blue for cart
    },
    landing: {
        bg: '#FFFFFF', // White for clean landing
        left: '#00E5FF', // Aqua blue
        textLeft: '#FFFFFF', // Keep text legible
        textLeftSpan: '#FF4081', // Pink highlight
        center: '#FF6E40', // Coral orange
        shopLink: '#651FFF', // Purple shop link
        centerButtons: '#FFD740', // Bright yellow for primary
        centerButtonsLink: '#FFFFFF', // White text
        centerButton2: '#1DE9B6', // Teal button
        centerButton2Link: '#FFFFFF', // Consistent white text
        storeButton: '#00E676', // Neon green
        aboutButton: '#FF6E40', // Coral orange
        right: '#651FFF', // Purple for contrast
        textRight: '#FFFFFF', // Legible white text
        textRightSpan: '#FF4081', // Highlight text
    },
    offeringSchema: {
        bg: 'rgba(255, 255, 255, 0.9)', // Light transparent white
        bgHover: 'rgba(255, 255, 255, 1)',
        icon: '#651FFF', // Purple icons
        iconHover: '#3D5AFE', // Blue hover
        title: '#FF4081', // Bright headings
        description: '#546E7A', // Neutral contrast
        styledLink: '#00B0FF', // Vibrant blue
        styledLinkHover: '#FFD740', // Hover yellow
    },
    offering: {
        bg: 'rgba(255, 255, 255, 0.8)', // Clean white
        introduction: 'rgba(255, 255, 255, 0.7)',
        introH1: '#3D5AFE', // Vivid blue
        introH2: '#FF4081', // Bright pink
        introP: '#546E7A', // Neutral text
        offers: 'rgba(0, 0, 0, 0.03)', // Light shadow
    },
    contact: {
        bg: '#FFFFFF', // Clean white
        title: '#FF6E40', // Coral orange
        description: '#00B0FF', // Sky blue
        socialLinks: '#FF4081', // Pink
        socialLink: '#FFD740', // Vibrant yellow
    },
    clientCounter: {
        bg: 'rgba(255, 255, 255, 0.9)',
        countWrapper: 'rgba(255, 255, 255, 0.8)',
        number: '#1DE9B6', // Teal numbers
        counter: '#FF6E40', // Coral accents
    },
    about: {
        bg: '#FFFFFF', // Clean background
        aboutSection: '#FFAB00', // Bright amber
        aboutSectionH1: '#651FFF', // Purple
        aboutSectionH2: '#00E676', // Green subheadings
        aboutSectionP: '#546E7A', // Neutral text
        faq: '#FFD740', // Vibrant yellow
        dropdown: '#FF4081', // Pink dropdown
        dropdownH1: '#00B0FF', // Bright blue
        answer: '#E0F7FA', // Aqua for FAQ answers
        answerP: '#37474F', // Dark blue-gray
    },
    footer: {
        bg: '#FFFFFF', // Clean footer background
        footWrapper: '#FFFFFF',
        section: '#00E5FF', // Aqua blue
        companyTitile: '#FF4081', // Pink title
        companyDescription: '#546E7A', // Neutral text
        sectionTitile: '#FF6E40', // Coral headings
        styledLink: '#FFD740', // Yellow links
        finalSection: '#651FFF', // Purple final section
        finalText: '#3D5AFE', // Bright blue text
        finalLink: '#1DE9B6', // Teal final links
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
