import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { FaInstagram, FaTelegram, FaYoutube, FaTwitter } from 'react-icons/fa';
import styled from 'styled-components';
import { colorContext } from '../../Context/ColorContextProvider';

const footerConfig = {
    name : '',
    cd: '',
    service: '',
}


const Footer = () => {

    const { config } = useContext(colorContext);

    return (
        <Container theme={config.footer}>
        <FooterWrapper theme={config.footer}>
            <Section theme={config.footer}>
                <CompanyTitle theme={config.footer}>ویولا</CompanyTitle>
                <CompanyDescription theme={config.footer}>
                    در شکلات خوشمزه بهترین شکلات‌های دست‌ساز را تجربه کنید. ما شکلات‌های سفارشی برای تمام مناسبت‌ها با طعم‌ها و طراحی‌های منحصر به فرد ارائه می‌دهیم.
                </CompanyDescription>
            </Section>

            <Section theme={config.footer}>
                <SectionTitle theme={config.footer}>ناوبری سایت</SectionTitle>
                <StyledLink theme={config.footer} to="/">خانه</StyledLink>
                <StyledLink theme={config.footer} to="/products">شکلات‌های ما</StyledLink>
                <StyledLink theme={config.footer} to="/about">درباره ما</StyledLink>
                <StyledLink theme={config.footer} to="/contact">تماس با ما</StyledLink>
            </Section>

            <Section theme={config.footer}>
                <SectionTitle theme={config.footer}>ما را دنبال کنید</SectionTitle>
                <StyledLink theme={config.footer} to="https://www.instagram.com" target="_blank">
                    <FaInstagram size={18} />
                </StyledLink>
                <StyledLink theme={config.footer} to="https://t.me" target="_blank">
                    <FaTelegram size={18} />
                </StyledLink>
                <StyledLink theme={config.footer} to="https://www.youtube.com" target="_blank">
                    <FaYoutube size={18} />
                </StyledLink>
                <StyledLink theme={config.footer} to="https://twitter.com" target="_blank">
                    <FaTwitter size={18} />
                </StyledLink>
            </Section>

            <Section theme={config.footer}>
                <SectionTitle theme={config.footer}>خدمات ما</SectionTitle>
                <StyledLink theme={config.footer} to="/custom-orders">سفارشات سفارشی</StyledLink>
                <StyledLink theme={config.footer} to="/gifts">هدایای شکلاتی</StyledLink>
                <StyledLink theme={config.footer} to="/subscriptions">برنامه‌های اشتراک</StyledLink>
                <StyledLink theme={config.footer} to="/corporate">هدایای شرکتی</StyledLink>
            </Section>
        </FooterWrapper>

        <FinalSection theme={config.footer}>
            <FinalText theme={config.footer}>
                &copy; 2024 طراحی شده توسط: <HighlightSpan>شکلات خوشمزه</HighlightSpan>
            </FinalText>
            <FinalLink theme={config.footer} to="/terms">شرایط و سیاست حفظ حریم خصوصی</FinalLink>
        </FinalSection>
    </Container>
    );
};

export default Footer;


const Container = styled.div`
    width: 100%;
    height: fit-content;
    display: flex;
    flex-direction: column;
    align-item: center;
    background:  ${({ theme }) => theme.bg};
    padding-top: 40px;
    overflow: hidden;
`;

const Section = styled.div`
    margin: auto;
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
    padding: 40px;
    height: 200px;
    // background-color:  ${({ theme }) => theme.section};

    gap: 25px;
    margin-bottom:15px;
        @media screen and (max-width: 991px) {
        padding: 0px;
        padding-top:80px;
    }
`;


const FooterWrapper = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    // background-color:  ${({ theme }) => theme.footWrapper};

    @media screen and (max-width: 991px) {
        flex-direction: column;
        align-items: center;
    }
`;


const CompanyTitle = styled.h1`
    color:  ${({ theme }) => theme.companyTitile};
    font-size: 35px;
    font-weight: bold;
    text-align: center;
    // padding: 10px;
`;

const CompanyDescription = styled.p`
    // padding: 10px;
    line-height: 22px;
    font-size: 14px;
    color:  ${({ theme }) => theme.companyDescription};
    text-align: center;
        @media screen and (max-width: 991px) {
        padding: 15px;
    }
`;

const SectionTitle = styled.h2`
    color:  ${({ theme }) => theme.sectionTitile};
    font-weight: 900;
    font-size: 20px;
`;

const StyledLink = styled(Link)`
    color:  ${({ theme }) => theme.styledLink};
    text-decoration: none;
    // padding: 8px;
    font-family: sans-serif;
    font-weight: 400;
    font-size: 14px;
    transition: all 0.1s ease-in;

    &:hover {
        color:  ${({ theme }) => theme.aboutSection};
        font-size: 16px;
    }
`;

const FinalSection = styled.div`
    width: 100%;
    height: fit-content;
    border-top: #bcbcbc4f solid 1px;
    display: flex;
    flex-direction: row;
    justify-content: space-around;
    transition: all 0.3s ease-in-out;
    // background-color:  ${({ theme }) => theme.finalSection};

    align-items: center;
    opacity: 0.4;
    padding: 15px;
        &:hover {
            opacity: 1;
        }
    @media screen and (max-width: 991px) {
        flex-direction: column;
        &:hover {
            opacity: 1;
        }
    }
`;

const FinalText = styled.p`
    // padding: 15px;
    color:  ${({ theme }) => theme.finalText};
    font-weight: bold;
`;

const HighlightSpan = styled.span`
    color: gold;
    cursor: pointer;
`;

const FinalLink = styled(Link)`
    // padding: 15px;
    font-weight: bold;
    text-decoration: none;
    color:  ${({ theme }) => theme.finalLink};
`;
