import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Admin from '../Dashboard/Admin';
import Customer from '../Dashboard/Customer';
import styled from 'styled-components';

// Styled Component
const DashboardContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    padding: 200px;
    max-width: 90%;
    margin: auto;
    background-color: #ff0000;
    border-radius: 102px;
    box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.15);
    min-height: 60vh;
`;

const Message = styled.p`
    font-family: 'Poppins', sans-serif;
    font-size: 16px;
    color: #333;
    text-align: center;
`;

const Dashboard = () => {
    const [responseMsg, setResponseMsg] = useState(null);
    const [role, setRole] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const checkRole = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/dashboard', { withCredentials: true });
                response.data.role ? setRole(response.data.role) : setResponseMsg('دسترسی ممنوع. نقش مجاز نیست.');
            } catch (error) {
                if (error.response && error.response.status === 403) {
                    setResponseMsg('دسترسی ممنوع. لطفا وارد شوید.');
                    navigate('/login');
                } else {
                    setResponseMsg('یک خطا رخ داده است. لطفا دوباره امتحان کنید.');
                }
            }
        };
        checkRole();
    }, [navigate]);

    return (
        <DashboardContainer>
            {role ? (
                role === 'admin' ? <Admin /> : <Customer />
            ) : (
                <Message>{responseMsg || 'در حال بارگذاری...'}</Message>
            )}
        </DashboardContainer>
    );
};

export default Dashboard;
