import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#ffffff',
        logoContainer: '#004D40',
        h1: '#FF7043',
        hamburger: '#004D40',
        hamDiv: '#B2DFDB',
        links: '#004D40',
        linksPhone: '#B2DFDB',
        linkLi: '#00796B',
        linkA: '#00796B',
        iconContainer: '#004D40',
        iconA: '#FF7043',
        profile: '#FF7043',
        pulse: '#FF7043',
        cart: '#00796B',
    },
    landing: {
        bg: '#E1F5FE',
        left: '#004D40',
        textLeft: '#FFFFFF',
        textLeftSpan: '#FF7043',
        center: '#FF7043',
        shopLink: '#00796B',
        centerButtons: '#FF7043',
        centerButtonsLink: '#ffffff',
        centerButton2: '#004D40',
        centerButton2Link: '#ffffff',
        storeButton: '#00796B',
        aboutButton: '#FF7043',
        right: '#00796B',
        textRight: '#FFFFFF',
        textRightSpan: '#FF7043',
    },
    offeringSchema: {
        bg: 'rgba(255, 255, 255, 0.8)',
        bgHover: 'rgba(255, 255, 255, 0.9)',
        icon: '#FF7043',
        iconHover: '#FF5722',
        title: '#212121',
        description: '#424242',
        styledLink: '#FF7043',
        styledLinkHover: '#BF360C',
    },
    offering: {
        bg: 'rgba(255, 255, 255, 0.5)',
        introduction: 'rgba(255, 255, 255, 0.5)',
        introH1: '#FF7043',
        introH2: '#37474F',
        introP: '#546E7A',
        offers: 'rgba(0, 0, 0, 0.03)',
    },
    contact: {
        bg: 'rgba(255, 255, 255, 0.8)',
        title: '#00796B',
        description: '#546E7A',
        socialLinks: '#FF7043',
        socialLink: '#FF5722',
    },
    clientCounter:{
        bg:'rgba(255, 255, 255, 0.8)',
        countWrapper:'rgba(255, 255, 255, 0.8)',
        number: '#FF5722',
        counter: '#00796B',
    },
    about: {
        bg: '#FAF3E0', // Warm cream for the section background
        aboutSection: '#FFF5E1', // Light beige for the main about section container
        aboutSectionH1: '#D2691E', // Rich chocolate brown for the main heading
        aboutSectionH2: '#8B4513', // Darker chocolate brown for subheadings
        aboutSectionP: '#5D4037', // Smooth cocoa brown for paragraph text
        faq: '#FFE4B5', // Muted sandy background for FAQ section
        dropdown: '#FFDAB9', // Peachy cream for dropdown background
        dropdownH1: '#8B0000', // Deep red-brown for FAQ dropdown headings
        answer: '#FFE4C4', // Light tan for FAQ answers
        answerP: '#6D4C41', // Coffee-like brown for FAQ answers text
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
