import React, { useContext, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { FaShoppingCart, FaUserCircle } from 'react-icons/fa';
import { Link, useLocation } from 'react-router-dom';
// import { ProductContext } from '../../Context/ProductContextProvider';

const shake = keyframes`
  0%, 100% { transform: rotate(0deg); }
  20%, 60% { transform: rotate(-15deg); }
  40%, 80% { transform: rotate(15deg); }
`;

const activeItems = keyframes`
  0%, 100% { transform: scale(1.1); }
  20%, 60% { transform: scale(1); color: goldenrod; }
  40%, 80% { transform: scale(1.2); }
`;

const pulse = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.1); color: #7d2f9ed3; }
`;

const Container = styled.div`
  max-height: 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: calc(100% - 245px);
  padding: 20px;
  border-radius: 16px;
  margin: 20px auto;
  background: rgba(142, 68, 173, 0.02);
  box-shadow: 0 4px 30px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(0px);
  -webkit-backdrop-filter: blur(0px);

  ${({ isDarkTheme }) =>
    isDarkTheme &&
    `
      background-color: #1a1a1a;
      color: white;
      width: 100%;
      margin: 0px;
      border-radius: 0px;
  `}
`;

const Hamburger = styled.div`
  width: 2rem;
  height: 2rem;
  position: fixed;
  top: 15px;
  left: 10px;
  z-index: 20;
  display: none;
  cursor: pointer;

  @media (max-width: 899px) {
    display: flex;
    flex-direction: column;
    justify-content: space-around;
  }

  div {
    width: 2rem;
    height: 0.25rem;
    background: linear-gradient(135deg, #a68ebd, #8e44ad);
    border-radius: 10px;
    transform-origin: 1px;
    transition: all 0.3s linear;

    &:nth-child(1) {
      transform: ${({ open }) => (open ? 'rotate(45deg)' : 'rotate(0)')};
    }
    &:nth-child(2) {
      transform: ${({ open }) => (open ? 'translateX(-500%)' : 'translateX(0)')};
      opacity: ${({ open }) => (open ? '0' : '1')};
    }
    &:nth-child(3) {
      transform: ${({ open }) => (open ? 'rotate(-45deg)' : 'rotate(0)')};
    }
  }
`;

const Logo = styled.div`
  color: #8e44ad;

  h1 {
    font-size: 24px;
    font-weight: bold;
  }

  ${({ isDarkTheme }) =>
    isDarkTheme &&
    `
      color: white;
  `}
`;

const Links = styled.ul`
  list-style: none;
  display: flex;
  gap: 30px;
  padding: 12px 10px;

  @media (max-width: 899px) {
    flex-direction: column;
    position: fixed;
    top: 0;
    right: 0;
    width: 250px;
    height: 100vh;
    border-radius: 30px 0 0 30px;
    transform: ${({ open }) => (open ? 'translateX(0)' : 'translateX(100%)')};
    transition: transform 0.3s ease;
    padding-top: 80px;
    backdrop-filter: blur(12px);
    background: rgba(166, 142, 189, 0.85);
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
    z-index: 1000;
  }
`;

const LinkItem = styled.li`
  padding: 5px 0;

  a {
    text-decoration: none;
    color: black;
    transition: color 0.3s ease;
    font-size: 18px;
    white-space: nowrap;
    position: relative;

    &:hover {
      color: #8e44ad;
    }

    &::after {
      content: '';
      position: absolute;
      left: 50%;
      bottom: -3px;
      transform: translateX(-50%);
      width: 0;
      height: 2px;
      background-color: #8e44ad;
      transition: width 0.3s ease;
    }

    &:hover::after {
      width: 100%;
    }
  }

  ${({ isDarkTheme }) =>
    isDarkTheme &&
    `
      a {
        color: white;

        &:hover {
          color: #34264e;
        }
      }
  `}
`;

const IconContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Icon = styled.div`
  font-size: 30px;
  transition: color 0.3s ease;
  margin: 10px;
  color: ${({ hasItems }) => (hasItems ? 'goldenrod' : 'rgb(150, 149, 149)')};

  &:hover {
    color: rgb(121, 79, 158);
    animation: ${shake} 0.4s ease-in-out;
  }

  ${({ hasItems }) =>
    hasItems &&
    `
      animation: ${activeItems} infinite 1s ease-in-out;
  `}
`;

const ProfileIcon = styled(Icon)`
  color: rgb(121, 79, 158);

  &:hover {
    color: lightgrey;
    animation: ${pulse} 0.9s ease-in-out infinite;
  }
`;

const Nav = () => {
  const [open, setOpen] = useState(false);
//   const { state } = useContext(ProductContext);
  const location = useLocation();

  const isDarkTheme = location.pathname === '/login' || location.pathname === '/register';

  return (
    <Container isDarkTheme={isDarkTheme}>
      <Hamburger open={open} onClick={() => setOpen(!open)}>
        <div></div>
        <div></div>
        <div></div>
      </Hamburger>
      <Logo isDarkTheme={isDarkTheme}>
        <h1>ویولا</h1>
      </Logo>
      <Links open={open}>
        <LinkItem isDarkTheme={isDarkTheme}>
          <Link to="/store">محصولات</Link>
        </LinkItem>
        <LinkItem isDarkTheme={isDarkTheme}>
          <Link to="/about">درباره ما</Link>
        </LinkItem>
        <LinkItem isDarkTheme={isDarkTheme}>
          <Link to="/">صفحه اصلی</Link>
        </LinkItem>
      </Links>
      <IconContainer>
        <Link to="/dashboard">
          <ProfileIcon />
        </Link>
        <Link to="/cart">
          {/* <Icon hasItems={state.selectedItems.length > 0} /> */}
        </Link>
      </IconContainer>
    </Container>
  );
};

export default Nav;
