import axios from 'axios';
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

// Styled Components
const Container = styled.div`
  padding: 40px 20px;
  max-width: 1200px;
  margin: 0 auto;
  background-color: #f7f8fb;
`;

const Title = styled.h1`
  text-align: center;
  margin-bottom: 40px;
  font-family: 'Poppins', sans-serif;
  font-size: 32px;
  font-weight: 600;
  color: #3d3d3d;
`;

const OrderCard = styled.div`
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  padding: 25px;
  margin-bottom: 30px;
  font-family: 'Roboto', sans-serif;
  transition: transform 0.3s ease-in-out, box-shadow 0.3s ease;

  h3 {
    margin: 30px 0px 10px 0px;
  }

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 25px rgba(0, 0, 0, 0.15);
  }
`;

const UserInfo = styled.div`
  margin-bottom: 20px;
  font-size: 16px;
  color: #4d4d4d;

  h2 {
    font-size: 18px;
    font-weight: 600;
    color: #1e1e1e;
  }

  p {
    margin: 5px 0;
  }
`;

const Address = styled.p`
  color: #888;
  font-size: 14px;
`;

const DateTime = styled.p`
  font-size: 14px;
  color: #777;
`;

const ProductList = styled.ul`
  list-style-type: none;
  padding: 0;
`;

const ProductItem = styled.li`
  background: #f3f3f3;
  padding: 12px;
  margin: 10px 0;
  border-radius: 8px;
  font-size: 14px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: background 0.3s ease;

  &:hover {
    background: #ebebeb;
  }
`;

const ProductName = styled.span`
  font-weight: 500;
  color: #333;
`;

const ProductDetails = styled.span`
  font-size: 14px;
  color: #555;
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 180px;
`;

const Status = styled.p`
  font-weight: 600;
  font-size: 16px;
  color: #4a90e2;
  text-transform: capitalize;
`;

const NoOrders = styled.p`
  font-size: 18px;
  color: #555;
  text-align: center;
`;

const Orders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/get-orders', { withCredentials: true });
        if (response.status === 200) {
          setOrders(response.data);
        }
      } catch (error) {
        console.log(error);
      }
    };
    fetchOrders();
  }, []);

  return (
    <>
      <Title>سفارشات</Title>
      <Container>
        {orders.length > 0 ? (
          orders.map((order, index) => (
            <OrderCard key={index}>
              <UserInfo>
                <h2>{order.user.firstName} {order.user.lastName}</h2>
                <p><strong>شماره تلفن:</strong> {order.user.phoneNumber}</p>
                <Status>{order.status}</Status>
              </UserInfo>
              <Address>
                <strong>آدرس:</strong> {order.address.area}, {order.address.neighbourhood}, {order.address.street}, {order.address.alleys}, {order.address.postalCode}, {order.address.plaque}
              </Address>
              <DateTime>
                <strong>تاریخ:</strong> {new Date(order.dateAndTime.date).toLocaleDateString()} ساعت {order.dateAndTime.time}
              </DateTime>

              <h3>محصولات سفارش داده شده:</h3>
              <ProductList>
                {order.orderedProducts.map((product, idx) => (
                  <ProductItem key={idx}>
                    <ProductName>{product.productName}</ProductName>
                    <ProductDetails>
                      قیمت: {product.productPrice} | تعداد: {product.quantity}
                    </ProductDetails>
                  </ProductItem>
                ))}
              </ProductList>
            </OrderCard>
          ))
        ) : (
          <NoOrders>سفارشی پیدا نشد.</NoOrders>
        )}
      </Container>
    </>
  );
};

export default Orders;
