import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
nav: {
    bg: '#F7F7F7', // Light background for a clean and soft look
    logoContainer: '#2C2C2C', // Dark gray for a strong but subtle contrast
    h1: '#2C2C2C', // Matching dark gray for text
    hamburger: '#5F5F5F', // Slightly lighter gray for hamburger icon
    hamDiv: '#F1F1F1', // Lighter background for the hamburger menu container
    links: '#3E3E3E', // Dark gray for link text to keep it readable
    linksPhone: '#F7F7F7', // Light background for the phone version links to match the theme
    linkLi: '#3E3E3E', // Dark gray for list items in navigation
    linkA: '#8E44AD', // Purple accent color for links for a rich and vibrant look
    iconContainer: '#333333', // Consistent dark color for the icons
    iconA: '#8E44AD', // Purple for the icon links to tie with the theme
    profile: '#8E44AD', // Profile icon color for consistency with the theme
    pulse: '#F39C12', // Orange/yellow for a glowing hover effect (pulse effect)
    cart: '#8E44AD', // Purple for the cart icon to match the theme
}

    }
}

const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
