import React, { useContext } from 'react';
import styled from 'styled-components';
import { FaInstagram, FaWhatsapp, FaTelegramPlane } from 'react-icons/fa';  // Import specific icons
import { colorContext } from '../../Context/ColorContextProvider';


const ContactContainer = styled.div`
  padding: 20px;
  max-width: 900px;
  margin: 0 auto;
  text-align: center;
  margin-bottom: 100px;
  margin-top: 100px;
  background-color: ${({ theme }) => theme.bg};
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 20px;
  color: ${({ theme }) => theme.title};
`;

const Description = styled.p`
  font-size: 1.2rem;
  margin-bottom: 30px;
  color: ${({ theme }) => theme.description};
`;

const SocialLinks = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-bottom: 30px;
  // background-color:${({ theme }) => theme.socialLinks} ;
`;

const SocialLink = styled.a`
  display: block;
  width: 50px;
  height: 50px;
  color: ${({ theme }) => theme.socialLink} ;
  font-size: 2rem;
  transition: transform 0.2s;
  
  &:hover {
    transform: scale(1.1);
  }
`;

const Contact = () => {

  const { config } = useContext(colorContext);

  return (
    <ContactContainer theme={config.contact}>
      <Title theme={config.contact}>تماس با ما</Title> {/* Persian for "Contact Us" */}

      <Description theme={config.contact}>شما می‌توانید از طریق پلتفرم‌های زیر با ما ارتباط برقرار کنید:</Description> {/* Persian for "You can contact us via the following platforms:" */}

      {/* Social Media Links */}
      <SocialLinks theme={config.contact}>
        <SocialLink theme={config.contact} href="https://instagram.com/yourprofile" target="_blank" rel="noopener noreferrer">
          <FaInstagram />
        </SocialLink>

        <SocialLink theme={config.contact} href="https://wa.me/yourwhatsappnumber" target="_blank" rel="noopener noreferrer">
          <FaWhatsapp />
        </SocialLink>

        <SocialLink theme={config.contact} href="https://t.me/yourtelegramusername" target="_blank" rel="noopener noreferrer">
          <FaTelegramPlane />
        </SocialLink>
      </SocialLinks>

      <p style={{color:`${config.con}`}}>ما منتظر پیام‌های شما هستیم!</p> {/* Persian for "We look forward to your messages!" */}
    </ContactContainer>
  );
};

export default Contact;
