import React, { useState, useEffect, useContext } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { colorContext } from '../../Context/ColorContextProvider';



const Register = () => {

    const { config } = useContext(colorContext);
    const navigate = useNavigate();
    const [data, setData] = useState({ name: '', phone: '', password: '' });
    const [responseMsg, setResponseMsg] = useState('');

    const onchangeHandler = (e) => {
        const { name, value } = e.target;
        setData({ ...data, [name]: value });
    };

    const registerUser = async () => {
        try {
            if (!data.name || !data.phone || !data.password) {
                setResponseMsg('تمام فیلدها الزامی است');
                return;
            }
            const response = await axios.post('http://localhost:8080/api/register', data, {
                withCredentials: true,
            });

            if (response.status === 201) {
                navigate(response.data.redirect);
            }
        } catch (error) {
            if (error.response) {
                const { status } = error.response;
                const message = error.response.data.message;

                if (status === 400) {
                    setResponseMsg(message || 'تمام فیلدها الزامی است.');
                } else if (status === 409) {
                    setResponseMsg(message || 'نام کاربری یا شماره قبلاً ثبت شده است.');
                } else if (status === 500) {
                    setResponseMsg(message || 'خطای داخلی سرور. لطفاً بعداً دوباره تلاش کنید.');
                } else {
                    setResponseMsg(message || 'خطای غیرمنتظره رخ داد.');
                }
            } else {
                setResponseMsg('خطای شبکه. لطفاً اتصال خود را بررسی کنید.');
            }
        }
    };

    useEffect(() => {
        const checkSession = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/check-session', {
                    withCredentials: true,
                });
                if (response.data.loggedIn && response.data.redirect) {
                    navigate(response.data.redirect);
                }
            } catch (error) {
                console.log(error);
            }
        };
        checkSession();
    }, [navigate]);

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            registerUser();
        }
    };

    return (
        <Wrapper theme = {config} onKeyPress={handleKeyPress}>
            <Left theme = {config}>
                <Circle theme = {config} className="circle1" />
                <Circle theme = {config} className="circle2" />
                <Circle theme = {config} className="circle3" />
                <Circle theme = {config} className="circle4" />
                <Welcome theme = {config}>! به فروشگاه ما خوش آمدید</Welcome>
                <Description theme = {config}>
                    لطفاً برای ادامه ثبت‌نام کنید. این یک فروشگاه آنلاین است که می‌توانید شکلات‌های سفارشی و همچنین بسته‌هایی برای هر مناسبت بخرید.
                </Description>
                <LinkText theme = {config}>
                    قبلاً حساب دارید؟ <StyledLink theme = {config} to="/login">ورود</StyledLink>
                </LinkText>
            </Left>
            <Right theme = {config}>
                <Container theme = {config}>
                    <Heading theme = {config}>ثبت نام</Heading>
                    <Input theme = {config} name="name" value={data.name} onChange={onchangeHandler} placeholder="نام" />
                    <Input theme = {config} name="phone" value={data.phone} onChange={onchangeHandler} placeholder="شماره تلفن" />
                    <Input theme = {config} type="password" name="password" value={data.password} onChange={onchangeHandler} placeholder="رمز عبور" />
                    <RegisterButton theme = {config} onClick={registerUser}>ثبت‌نام</RegisterButton>
                    {responseMsg && <ResponseMsg theme = {config}>{responseMsg}</ResponseMsg>}
                </Container>
            </Right>
        </Wrapper>
    );
};

export default Register;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const slideIn = keyframes`
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
`;

const riseUp = keyframes`
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
`;

// Styled Components
const Wrapper = styled.div`
  display: flex;
  height: 100vh;
  background: #1a1a1a;
  overflow: hidden;
  padding-top: 100px;
  flex-direction: column;

  @media (min-width: 769px) {
    flex-direction: row;
  }
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  color: #ffffff;
  padding: 20px;
  text-align: center;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: radial-gradient(circle, rgba(94, 75, 139, 0.2) 20%, rgba(94, 75, 139, 0.1) 40%, transparent 50%);
    pointer-events: none;
    z-index: 0;
  }
`;

const Circle = styled.div`
  position: absolute;
  border-radius: 50%;
  background-color: rgba(94, 75, 139, 0.1);
  z-index: 0;

  &.circle1 {
    width: 50px;
    height: 50px;
    top: 10%;
    left: 10%;
  }

  &.circle2 {
    width: 30px;
    height: 30px;
    top: 60%;
    left: 20%;
  }

  &.circle3 {
    width: 40px;
    height: 40px;
    top: 30%;
    right: 15%;
  }

  &.circle4 {
    width: 60px;
    height: 60px;
    bottom: 10%;
    right: 5%;
  }
`;

const Welcome = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(255, 255, 255, 0.3);
  animation: ${slideIn} 1s ease-out;
  font-family: 'Playfair Display', serif;
`;

const Description = styled.p`
  font-size: 1rem;
  max-width: 600px;
  opacity: 0;
  animation: ${fadeIn} 1s 0.5s forwards;
  font-family: 'Poppins', sans-serif;
  text-align: center;
  color: #f0f0f0;
  margin: 10px 0;
  line-height: 1.4;
  border-radius: 10px;
  padding: 10px;
`;

const LinkText = styled.p`
  font-size: 1rem;
`;

const StyledLink = styled(Link)`
  color: #ffffff;
  text-decoration: underline;
`;

const Right = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
`;

const Container = styled.div`
  background: #ffffff;
  padding: 20px;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  width: 90%;
  max-width: 350px;
  transform: translateY(20px);
  opacity: 0;
  animation: ${riseUp} 1s ease-out forwards;
  text-align: center;
`;

const Input = styled.input`
  width: calc(100% - 20px);
  padding: 10px;
  margin: 10px 0;
  border: 2px solid #5e4b8b;
  border-radius: 50px;
  font-size: 14px;
  color: #333;
  background: rgba(255, 255, 255, 0.9);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  animation: ${fadeIn} 1s forwards;

  &:hover {
    border-color: #d9c9e5;
    background: rgba(255, 255, 255, 1);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    transform: translateY(-2px);
  }

  &:focus {
    outline: none;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.4);
    transform: scale(1.05);
  }
`;

const RegisterButton = styled.button`
  width: 100%;
  padding: 12px;
  margin-top: 15px;
  border: none;
  border-radius: 50px;
  font-size: 16px;
  color: white;
  background: #5e4b8b;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
  cursor: pointer;
  transition: all 0.3s ease;
  animation: ${fadeIn} 1s forwards;

  &:hover {
    background: #d9c9e5;
    transform: scale(1.03);
