import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#f6fafe   ', // Very light blue background for a soft, fresh feel
        logoContainer: '#1A374D', // Dark blue for a strong, professional look
        h1: '#FF6F61', // Matching dark blue for headings to maintain consistency
        hamburger: '#3A5A7D', // Muted blue-gray for the hamburger icon for a refined touch
        hamDiv: '#D4E6F1', // Light, pastel blue background for the hamburger menu container
        links: '#1A374D', // Dark blue links for readability and elegance
        linksPhone: '#D4E6F1', // Light background for phone links to maintain uniformity
        linkLi: '#5C6BC0', // Dark blue for list items to tie everything together
        linkA: '#5C6BC0', // Soft indigo-blue for the links, providing an inviting contrast
        iconContainer: '#1A374D', // Dark blue icon containers for uniformity
        iconA: '#5C6BC0', // Accent blue for icons, matching link colors
        profile: '#5C6BC0', // Soft indigo-blue for profile icons, reinforcing the theme
        pulse: '#FF6F61', // Coral orange for hover effect, providing a lively pop
        cart: '#5C6BC0', // Accent blue for cart icon to match the overall theme
    },
    landing: {
        bg: '',
        left: '',
        textLeft: '',
        
    },
}

const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
