import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
// import { UserContext } from '../../Context/UserContextProvider';
import styled, { keyframes } from 'styled-components';

// Keyframes for animations
const fadeIn = keyframes`
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
`;

const slideIn = keyframes`
    from {
        transform: translateX(-100%);
    }
    to {
        transform: translateX(0);
    }
`;

const riseUp = keyframes`
    from {
        transform: translateY(20px);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
`;

// Styled components
const Wrapper = styled.div`
    display: flex;
    height: 100vh;
    background: #1a1a1a;
    overflow: hidden;
`;

const Left = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: #ffffff;
    padding: 10px;
    text-align: center;
    position: relative;

    &::before {
        content: '';
        position: absolute;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        background: radial-gradient(circle, rgba(94, 75, 139, 0.2) 20%, rgba(94, 75, 139, 0.1) 40%, transparent 50%);
        pointer-events: none;
        z-index: 0;
    }
`;

const Circle = styled.div`
    position: absolute;
    border-radius: 50%;
    background-color: rgba(94, 75, 139, 0.1);
    z-index: 0;
`;

const Circle1 = styled(Circle)`
    width: 50px;
    height: 50px;
    top: 5%;
    left: 5%;
`;

const Circle2 = styled(Circle)`
    width: 30px;
    height: 30px;
    top: 70%;
    left: 20%;
`;

const Circle3 = styled(Circle)`
    width: 40px;
    height: 40px;
    top: 30%;
    right: 10%;
`;

const Circle4 = styled(Circle)`
    width: 60px;
    height: 60px;
    bottom: 10%;
    right: 5%;
`;

const Welcome = styled.h1`
    font-size: 3rem;
    margin-bottom: 15px;
    text-shadow: 2px 2px 4px rgba(255, 255, 255, 0.3);
    animation: ${slideIn} 1s ease-out;
    font-family: 'Playfair Display', serif;
`;

const Description = styled.p`
    font-size: 1rem;
    max-width: 400px;
    opacity: 0;
    animation: ${fadeIn} 1s 0.5s forwards;
    font-family: 'Poppins', sans-serif;
    color: #f0f0f0;
    margin: 10px 0;
    line-height: 1.4;
    padding: 10px;
`;

const LinkWrapper = styled.div`
    margin-top: 15px;
    color: #5e4b8b;
    text-decoration: none;
`;

const Right = styled.div`
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
`;

const Container = styled.div`
    background: #ffffff;
    padding: 20px;
    border-radius: 20px;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
    width: 80%;
    max-width: 350px;
    transform: translateY(20px);
    opacity: 0;
    animation: ${riseUp} 1s ease-out forwards;
    text-align: center;
`;

const Heading = styled.h2`
    font-size: 1.8rem;
    margin-bottom: 20px;
    color: #5e4b8b;
    animation: ${fadeIn} 1s forwards;
    text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
`;

const Input = styled.input`
    width: 90%;
    padding: 10px;
    margin: 8px 0;
    border: 2px solid #5e4b8b;
    border-radius: 50px;
    font-size: 14px;
    color: #333;
    background: rgba(255, 255, 255, 0.9);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
    animation: ${fadeIn} 1s forwards;

    &:hover {
        border-color: #d9c9e5;
        background: rgba(255, 255, 255, 1);
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
        transform: translateY(-2px);
    }

    &:focus {
        outline: none;
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.4);
        transform: scale(1.02);
    }
`;

const RegisterButton = styled.button`
    width: 100%;
    padding: 12px;
    margin-top: 20px;
    border: none;
    border-radius: 50px;
    font-size: 16px;
    color: white;
    background: #5e4b8b;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.15);
    cursor: pointer;
    transition: all 0.3s ease;
    animation: ${fadeIn} 1s forwards;

    &:hover {
        background: #d9c9e5;
        transform: scale(1.03);
        box-shadow: 0 15px 25px rgba(0, 0, 0, 0.2);
    }
`;

const ResponseMsg = styled.div`
    margin-top: 20px;
    color: red;
    font-size: 1rem;
    opacity: 0;
    animation: ${fadeIn} 1s 0.5s forwards;
`;

const Login = () => {
    const { userInfo, dispatchInfo } = useContext(UserContext);
    const navigate = useNavigate();
    const [data, setData] = useState({
        name: '',
        password: ''
    });
    const [responseMsg, setResponseMsg] = useState('');

    const onchangeHandler = (e) => {
        const { name, value } = e.target;
        setData({
            ...data,
            [name]: value,
        });
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/login', data, {
                withCredentials: true
            })
            if (response.status === 200) {
                dispatchInfo({ type: 'LOGIN' })
                const timer = setTimeout(() => {
                    navigate(response.data.redirect)
                }, 1000);
                return () => clearTimeout(timer);
            }
        } catch (error) {
            if (error.response) {
                const { status } = error.response;
                const message = error.response.data.message;
                if (status === 400) {
                    setResponseMsg(message || 'تمام فیلدها الزامی است.');
                } else if (status === 401) {
                    setResponseMsg(message || 'نام کاربری یا رمز عبور صحیح نمیباشد');
                } else if (status === 500) {
                    setResponseMsg(message || 'خطای داخلی سرور. لطفاً بعداً دوباره تلاش کنید.');
                } else {
                    setResponseMsg(message || 'خطای غیرمنتظره رخ داد.');
                }
            } else {
                setResponseMsg('خطای شبکه. لطفاً اتصال خود را بررسی کنید.');
            }
        }
    };

    useEffect(() => {
        const checkSession = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/check-session', {
                    withCredentials: true,
                });
                if (response.data.loggedIn && response.data.redirect) {
                    navigate(response.data.redirect);
                }
            } catch (error) {
                console.log(error);
            }
        };
        checkSession();
    }, [navigate]);

    const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleLogin();
        }
    };

    return (
        <Wrapper onKeyPress={handleKeyPress}>
            <Left>
                <Circle1 />
                <Circle2 />
                <Circle3 />
                <Circle4 />
                <Welcome> ! به فروشگاه ما خوش آمدید</Welcome>
                <Description> برای خرید شکلات‌های سفارشی خود، وارد حساب کاربری‌تان شوید.</Description>
                <LinkWrapper>
                    <Link to="/register">حساب کاربری ندارید؟ ثبت‌نام کنید</Link>
                </LinkWrapper>
            </Left>
            <Right>
                <Container>
                    <Heading>ورود به حساب</Heading>
                    <Input
                        type="text"
                        name="name"
                        value={data.name}
                        placeholder="نام کاربری"
                        onChange={onchangeHandler}
                    />
                    <Input
                        type="password"
                        name="password"
                        value={data.password}
                        placeholder="رمز عبور"
                        onChange={onchangeHandler}
                    />
                    <RegisterButton onClick={handleLogin}>
                        ورود به حساب
                    </RegisterButton>
                    {responseMsg && <ResponseMsg>{responseMsg}</ResponseMsg>}
                </Container>
            </Right>
        </Wrapper>
    );
};

export default Login;
