import React, { useContext } from 'react';
import { Container, Introduction, Offers } from '../Offers/Offers';
import { colorContext } from '../../Context/ColorContextProvider';
import OfferingsSchema from '../Offers/OfferSchema';

const WhyUs = () => {
    const { config } = useContext(colorContext);
    const data = {

        1: {
            title: '.1',
            content: 'ما انواع شکلات‌های سفارشی و دست‌ساز را مطابق با سلیقه شما تولید می‌کنیم. طعم و طرح دلخواهتان را انتخاب کنید.',
            path: '/services'
        },
        2: {
            title: '.2',
            content: 'باکس‌های شکلات لوکس و شکیل، مناس ب برای مناسب ت‌هامناسبت‌ه امناسبت‌هامن اسبت‌هامناس بت‌هامناسب ت‌هامناسب ت‌هامناس بت‌هامنا سبت‌هامناسب  ت‌هامنا سبت‌هامناسبت‌ ها مناسبت‌هامناسبت‌هامناسبت‌ها و هدیه‌های خاص.',
            path: '/services'
        },
        3: {
            title: '.3',
            content: 'مجموعه‌ای از شکلات‌های ویژه مخصوص تعطیلات و فصل‌ها، با طعم و تزئینات فصلی.',
            path: '/services'
        },
    };
    

    return (
        <Container>
            <Introduction theme={config.offering}>
            <h1>چرا ما را انتخاب کنید</h1>
            <h2>چرا مشتریان ما را انتخاب می‌کنند</h2>
            <p>مشتریان ما را برای کیفیت برتر، طراحی‌های منحصر‌به‌فرد و تعهد به ارائه بهترین خدمات انتخاب می‌کنند. ما شکلات‌های سفارشی و باکیفیتی را برای شما فراهم می‌کنیم.</p>
            </Introduction>
            <Offers>
            <OfferingsSchema
                    title={data[1].title}
                    content={data[1].content}
                    goto={data[1].path}
                />
                <OfferingsSchema
                    title={data[2].title}
                    content={data[2].content}
                    goto={data[2].path}
                />
                <OfferingsSchema
                    title={data[3].title}
                    content={data[3].content}
                    goto={data[3].path}
                />
            </Offers>
        </Container>

    );
};

export default WhyUs;
