import React, { useContext } from 'react';
import styled from 'styled-components';
import OfferingsSchema from './OfferSchema';

import { colorContext } from '../../Context/ColorContextProvider';


const data = {

    customChocolates: {
        title: 'سفارش شکلات سفارشی',
        content: 'ما انواع شکلات‌های سفارشی و دست‌ساز را مطابق با سلیقه شما تولید می‌کنیم. طعم و طرح دلخواهتان را انتخاب کنید.',
        path: '/services'
    },
    giftBoxes: {
        title: 'باکس‌های هدیه',
        content: 'باکس‌های شکلات لوکس و شکیل، مناس ب برای مناسب ت‌هامناسبت‌ه امناسبت‌هامن اسبت‌هامناس بت‌هامناسب ت‌هامناسب ت‌هامناس بت‌هامنا سبت‌هامناسب ت‌هامناسب ت‌هامنا سبت‌هامناسبت‌ ها مناسبت‌هامناسبت‌هامناسبت‌ها و هدیه‌های خاص.',
        path: '/services'
    },
    seasonalSpecials: {
        title: 'شکلات‌های ویژه فصلی',
        content: 'مجموعه‌ای از شکلات‌های ویژه مخصوص تعطیلات و فصل‌ها، با طعم و تزئینات فصلی.',
        path: '/services'
    },
    chocolateBouquets: {
        title: 'دسته گل‌های شکلاتی',
        content: 'با دسته گل‌های شکلاتی ما، یک هدیه خلاقانه و خوشمزه را تجربه کنید. مناسب برای هر مناسبت.',
        path: '/services'
    },
    chocolateWorkshops: {
        title: 'کارگاه‌های آموزشی شکلات‌سازی',
        content: 'کارگاه‌های ما شما را با اصول و فنون شکلات‌سازی آشنا می‌کند. لذت تجربه ساخت شکلات را با ما داشته باشید.',
        path: '/services'
    }
};

const description = '>در [نام فروشگاه شما]، ما تخصصی در تولید شکلات‌های سفارشی و هدیه‌های خاص داریم.با ما طعم و ظرافت را تجربه کنید.'

const OffersComponent = () => {

    const { config } = useContext(colorContext);


    return (
        <Container theme={config}>
            <Introduction theme={config}>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>{description}</p>
            </Introduction>
            <Offers theme={config}>
                <OfferingsSchema
                    imgSrc={data.customChocolates.img}
                    title={data.customChocolates.title}
                    content={data.customChocolates.content}
                    goto={data.customChocolates.path}
                />
                <OfferingsSchema
                    imgSrc={data.giftBoxes.img}
                    title={data.giftBoxes.title}
                    content={data.giftBoxes.content}
                    goto={data.giftBoxes.path}
                />
                <OfferingsSchema
                    imgSrc={data.seasonalSpecials.img}
                    title={data.seasonalSpecials.title}
                    content={data.seasonalSpecials.content}
                    goto={data.seasonalSpecials.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateBouquets.img}
                    title={data.chocolateBouquets.title}
                    content={data.chocolateBouquets.content}
                    goto={data.chocolateBouquets.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateWorkshops.img}
                    title={data.chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateWorkshops.img}
                    title={data.chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
            </Offers>
        </Container>
    );
};

export default OffersComponent;

export const Container = styled.div`
    width: 100%;
    height: fit-content;
    /* background-color: ${({ theme }) => theme.bg}; */
    background-color: ${({ theme }) => theme.bg};
    padding-top: 100px;
    padding-bottom: 70px;
    overflow: hidden;
    /* background-color: red; */


    @media screen and (max-width: 991px) {
        margin-top: 50px;
    }

`;

 export const Introduction = styled.div`
    width: 100%;
    height: fit-content;
    /* background-color: ${({ theme }) => theme.introduction}; */
    margin-bottom: 100px;
    text-align: right;

    h1 {
        padding: 30px 80px 0px 0px;
        font-size: 45px;
        color: ${({ theme }) => theme.introH1};
    }

    h2 {
        padding: 20px 80px 10px 0px;
        font-size: 28px;
        color: ${({ theme }) => theme.introH2};
    }

    p {
        padding: 0px 80px 10px 0px;
        font-size: 17px;
        color: ${({ theme }) => theme.introP};
    }

    @media screen and (max-width: 991px) {

        margin-bottom: 0px;

        h1 {
            padding: 10px 30px 0px 0px ;
            font-size: 32px;
        }

        h2 {
            padding: 10px 30px 0px 0px ;
            font-size: 22px;
        }

        p {
            padding: 10px 30px 0px 0px ;
            font-size: 16px;
            word-wrap: wrap;
            width: 80%;
            // background-color: red;
            margin-left: auto;
        }
    }
    
`;

export const Offers = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 18px;
    transition: 1.5s ease;
    margin-top: 40px;
    background-color: ${({ theme }) => theme.offers};

    @media screen and (min-width: 991px) {
        width: 90%;
        margin: auto;
        flex-wrap: wrap;
        flex-direction: row;
        justify-content: space-between;
    }
    @media screen and (max-width: 375px) {
        gap: 10px;

    //  div {
    //     width: 90%;
    //     padding: 15px;
    //     margin: 10px 0;
    //     }
    }
`;
