import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
// import Overview from './Overview';

const Admin = () => {
    const navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const [responseMsg, setResponseMsg] = useState('');
    const [beingEdited, setBeingEdited] = useState([]);
    const [editedData, setEditedData] = useState({});
    const [selectedSection, setSelectedSection] = useState('overview');

    const [data, setData] = useState({
        name: '',
        price: 0,
        description: '',
        stock: 0,
        image: null, // New field for the image file
    });

    const logout = async () => {
        try {
            const response = await axios.post('http://localhost:8080/api/logout', {}, { withCredentials: true });
            if (response.status === 200 && response.data.redirect) {
                navigate(response.data.redirect);
            }
        } catch (error) {
            console.error('Error during logout:', error.response ? error.response.data.message : error);
        }
    };

    const onchangeHandler = (e) => {
        const { name, value } = e.target;
        setData({
            ...data,
            [name]: name === 'price' || name === 'stock' ? Number(value) : value,
        });
    };

    const onEditChangeHandler = (e, productName) => {
        const { name, value, files } = e.target;
        setEditedData({
            ...editedData,
            [productName]: {
                ...editedData[productName],
                [name]: name === 'image' ? files[0] : name === 'price' || name === 'stock' ? Number(value) : value,
            },
        });
    };

    useEffect(() => {
        const getProduct = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/get-products');
                if (response.status === 200) {
                    setProducts([...response.data]);
                }
            } catch (error) {
                if (error.response) {
                    const status = error.response.status;
                    if (status === 500) {
                        setResponseMsg('یک خطای غیرمنتظره رخ داده است.');
                    }
                }
            }
        };
        getProduct();
    }, []);



    const editProduct = (product) => {
        if (beingEdited.includes(product.name)) {
            setBeingEdited(beingEdited.filter((p) => p !== product.name));
        } else {
            setBeingEdited([...beingEdited, product.name]);
            setEditedData({ ...editedData, [product.name]: product });
        }
    };


    const deleteProduct = async (productId) => {
        try {
            const response = await axios.delete(`http://localhost:8080/api/delete-product/${productId}`, {
                withCredentials: true,
            });
            if (response.status === 200) {
                setProducts(products.filter(product => product._id !== productId));
            }
        } catch (error) {
            if (error.response) {
                setResponseMsg(error.response.data.message || 'خطا در حذف محصول');
            } else {
                setResponseMsg('خطای شبکه. لطفا اتصال خود را بررسی کنید.');
            }
        }
    };

    const SelectionHandler = (selection) => {
        setSelectedSection(selection)
    }
    useEffect(() => {
        if (responseMsg) {
            const timer = setTimeout(() => {
                setResponseMsg(""); // Clear the message
            }, 5000);
            return () => clearTimeout(timer);
        }
    }, [responseMsg]);

    const AddItem = async () => {
        const formData = new FormData();
        formData.append('name', data.name);
        formData.append('price', data.price);
        formData.append('description', data.description);
        formData.append('stock', data.stock);
        formData.append('image', data.image); // Append image file

        try {
            const response = await axios.post('http://localhost:8080/api/admin-restock', formData, {
                withCredentials: true,
                headers: {
                    'Content-Type': 'multipart/form-data', // Important for FormData
                },
            });

            if (response.status === 201) {
                const getProduct = async () => {
                    try {
                        const response = await axios.get('http://localhost:8080/api/get-products');
                        if (response.status === 200) {
                            setProducts([...response.data]);
                        }
                    } catch (error) {
                        handleErrorResponse(error);
                    }
                };
                getProduct();

                setData({
                    name: '',
                    price: 0,
                    description: '',
                    stock: 0,
                    image: null, // Reset image field
                });
            }
        } catch (error) {
            handleErrorResponse(error);
        }
    };

    const handleErrorResponse = (error) => {
        if (error.response) {
            const { message } = error.response.data;
            const status = error.response.status;
            if ([400, 403, 404, 409].includes(status)) {
                setResponseMsg(message);
            } else if (status === 500) {
                setResponseMsg('خطای داخلی سرور. لطفا بعدا دوباره تلاش کنید.');
            } else {
                setResponseMsg('یک خطای غیرمنتظره رخ داده است.');
            }
        } else {
            setResponseMsg('خطای شبکه. لطفا اتصال خود را بررسی کنید.');
        }
    };

    const onImageChange = (e) => {
        setData({ ...data, image: e.target.files[0] }); // Set image file
    };


    const saveChanges = async (productName) => {
        const productToUpdate = editedData[productName];
        const productId = productToUpdate._id;

        const formData = new FormData();
        formData.append('name', productToUpdate.name);
        formData.append('price', productToUpdate.price);
        formData.append('description', productToUpdate.description);
        formData.append('stock', productToUpdate.stock);
        if (productToUpdate.image) {
            formData.append('image', productToUpdate.image);
        }

        try {
            const response = await axios.patch(`http://localhost:8080/api/update-products/${productId}`, formData, {
                withCredentials: true,
                headers: {
                    'Content-Type': 'multipart/form-data', // Important for FormData
                },
            });
            if (response.status === 200) {
                setProducts(products.map(product => product._id === productId ? response.data : product));
                setBeingEdited(beingEdited.filter(name => name !== productName));
                setEditedData((prev) => ({ ...prev, [productName]: {} }));
            }
        } catch (error) {
            if (error.response) {
                const { message } = error.response.data;
                setResponseMsg(message || 'خطا در به‌روزرسانی محصول');
            }
        }
    };
    return (
<AdminWrapper>
    <ResponseMsg>{responseMsg}</ResponseMsg>
    <Container>
        <Sidebar>
            <SidebarHeading>مدیریت</SidebarHeading>
            <SidebarList>
                <SidebarListItem onClick={() => setSelectedSection('overview')}>بررسی کلی</SidebarListItem>
                <SidebarListItem onClick={() => setSelectedSection('orders')}>سفارشات</SidebarListItem>
                <SidebarListItem onClick={() => setSelectedSection('products')}>محصولات</SidebarListItem>
                <SidebarListItem onClick={logout}>خروج</SidebarListItem>
            </SidebarList>
        </Sidebar>
        <Divider />
        <Content>
            {/* {selectedSection === 'overview' && <Overview selectFunction={SelectionHandler} />} */}
            {selectedSection === 'orders' && <h2>سفارشات</h2>}
            {selectedSection === 'products' && (
                <div>
                    <AddItemContainer>
                        <AddItemHeading>اضافه کردن محصول جدید</AddItemHeading>
                        <InputGroup>
                            <InputLabel>نام محصول</InputLabel>
                            <Input value={data.name} onChange={onchangeHandler} name='name' type='text' />
                        </InputGroup>
                        {/* Similar for other inputs */}
                        <Button onClick={AddItem}>اضافه کردن</Button>
                    </AddItemContainer>
                    <ProductList>
                        {/* Render the product list */}
                    </ProductList>
                </div>
            )}
        </Content>
    </Container>
</AdminWrapper>
    );
};

