import React, { createContext, useReducer } from 'react';


const initialState = {
    nav: {
        bg: '#F4F1FB', // Light lavender for the background
        logoContainer: '#A37AC9', // Soft purple for the logo container
        h1: '#5A4E8F', // Deep muted purple for text
        hamburger: '#C7B6E2', // Pale purple for the hamburger menu
        hamDiv: '#F2E9FC', // Almost white with a hint of purple for a clean divider
        links: '#8C77B7', // Medium purple for link text
        linksPhone: '#A37AC9', 
        linkLi: '#C7B6E2',
        linkA: '#5A4E8F',
        iconContainer: '#A37AC9',
        iconA: '#8C77B7',
        profile: '#5A4E8F',
        pulse: '#C7B6E2',
        cart: '#A37AC9',
    },
    landing: {
        bg: '#F4F1FB',
        left: '#E3D9F6', // Soft purple gradient for the left section
        textLeft: '#5A4E8F', // Rich purple for text
        textLeftSpan: '#8C77B7', // Medium purple for highlighted text
        center: '#A37AC9', // Vibrant purple for attention-grabbing sections
        shopLink: '#5A4E8F',
        centerButtons: '#C7B6E2',
        centerButtonsLink: '#FFFFFF',
        centerButton2: '#8C77B7',
        centerButton2Link: '#FFFFFF',
        storeButton: '#5A4E8F',
        aboutButton: '#C7B6E2',
        right: '#E3D9F6',
        textRight: '#5A4E8F',
        textRightSpan: '#8C77B7',
    },
    offeringSchema: {
        bg: 'rgba(244, 241, 251, 0.9)', // Transparent lavender for the background
        bgHover: 'rgba(244, 241, 251, 1)',
        icon: '#8C77B7',
        iconHover: '#A37AC9',
        title: '#5A4E8F',
        description: '#8C77B7',
        styledLink: '#C7B6E2',
        styledLinkHover: '#A37AC9',
    },
    offering: {
        bg: 'rgba(244, 241, 251, 0.8)',
        introduction: 'rgba(244, 241, 251, 0.7)',
        introH1: '#5A4E8F',
        introH2: '#8C77B7',
        introP: '#716BA2', // Muted purple for paragraph text
        offers: 'rgba(227, 217, 246, 0.03)', // Subtle background for offers
    },
    contact: {
        bg: '#F4F1FB',
        title: '#5A4E8F',
        description: '#8C77B7',
        socialLinks: '#C7B6E2',
        socialLink: '#A37AC9',
    },
    clientCounter: {
        bg: 'rgba(244, 241, 251, 0.9)',
        countWrapper: 'rgba(244, 241, 251, 0.8)',
        number: '#A37AC9',
        counter: '#8C77B7',
    },
    about: {
        bg: '#F4F1FB',
        aboutSection: '#E3D9F6',
        aboutSectionH1: '#5A4E8F',
        aboutSectionH2: '#8C77B7',
        aboutSectionP: '#716BA2',
        faq: '#C7B6E2',
        dropdown: '#A37AC9',
        dropdownH1: '#5A4E8F',
        answer: '#E3D9F6',
        answerP: '#8C77B7',
    },
    footer: {
        bg: '#F4F1FB',
        footWrapper: '#F4F1FB',
        section: '#5A4E8F',
        companyTitile: '#8C77B7',
        companyDescription: '#716BA2',
        sectionTitile: '#A37AC9',
        styledLink: '#C7B6E2',
        finalSection: '#E3D9F6',
        finalText: '#5A4E8F',
        finalLink: '#8C77B7',
    }
};


const reducer = (state, action) => {
    switch (action.type) {

        default:
            return state
    }
}

export const colorContext = createContext();
const ColorContextProvider = ({ children }) => {

    const [config, dispatchConfig] = useReducer(reducer, initialState)

    return (
        <colorContext.Provider value={{ config, dispatchConfig }}>
            {children}
        </colorContext.Provider>
    );
};

export default ColorContextProvider;
