import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import { colorContext } from './ColorContext';  // Import the context

// Keyframes for animations
const fadeIn = keyframes`
  0% {
    opacity: 0;
    transform: translateY(30px);
  }
  100% {
    opacity: 1;
    transform: translateY(0);
  }
`;

const fadeInContainer = keyframes`
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
`;

const float = keyframes`
  0% {
    transform: translate(-50%, -50%) translateY(0);
  }
  50% {
    transform: translate(-50%, -50%) translateY(-30px);
  }
  100% {
    transform: translate(-50%, -50%) translateY(0);
  }
`;

// Component
const Landing = () => {
  const { config } = useContext(colorContext); // Access the context

  return (
    <Container bgColor={config.landing.bg}>
      <Left bgColor={config.landing.cardBg}>
        <TextLeft color={config.landing.textPrimary}>
          V <span className="i">I</span>
        </TextLeft>
      </Left>

      <Center color={config.landing.textPrimary}>
        <ShopLink color={config.landing.linkText} className="shopLink" href="#shop">V</ShopLink>
        <ShopLink color={config.landing.linkText} className="shopLink2" href="#shop">I</ShopLink>
        <ShopLink color={config.landing.linkText} className="shopLink3" href="#shop">O</ShopLink>
        <ShopLink color={config.landing.linkText} className="shopLink4" href="#shop">L</ShopLink>
        <ShopLink color={config.landing.linkText} className="shopLink5" href="#shop">A</ShopLink>
        O
      </Center>

      <CenterButtons>
        <Link to="/store">
          <StoreButton bgColor={config.landing.btnBg} textColor={config.landing.btnText}>
            فروشگاه
          </StoreButton>
        </Link>
      </CenterButtons>

      <CenterButtons2>
        <Link to="/about">
          <AboutButton bgColor={config.landing.subHeader}>
            درباره ما
          </AboutButton>
        </Link>
      </CenterButtons2>

      <Right bgColor={config.landing.cardBg}>
        <TextRight color={config.landing.textPrimary}>
          L <span className="a">A</span>
        </TextRight>
      </Right>
    </Container>
  );
};

// Styled Components with props to use context values

const Container = styled.div`
  display: flex;
  height: 100vh;
  width: 100%;
  position: relative;
  opacity: 0;
  font-family: 'Trebuchet MS', 'Lucida Sans Unicode', 'Lucida Grande', 'Lucida Sans', Arial, sans-serif;
  background-color: ${props => props.bgColor}; // Use bgColor prop
  animation: ${fadeInContainer} 2s ease-in-out forwards;
`;

const Left = styled.div`
  flex: 1;
  height: 100%;
  backdrop-filter: blur(18px);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  background-color: ${props => props.bgColor}; // Use bgColor prop
`;

const Right = styled.div`
  flex: 1;
  height: 100%;
  backdrop-filter: blur(18px);
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  background-color: ${props => props.bgColor}; // Use bgColor prop
`;

const Center = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  font-family: 'Times New Roman', Times, serif;
  transform: translate(-50%, -50%);
  font-size: 300px;
  font-weight: bold;
  color: ${props => props.color}; // Use color prop
  text-shadow: 4px 4px 40px rgba(0, 0, 0, 0.3);
  z-index: 2;
  opacity: 0;
  animation: ${fadeIn} 3s ease-in-out forwards, ${float} 2s infinite ease-in-out;
  cursor: pointer;
`;

const ShopLink = styled.p`
  position: absolute;
  font-size: 20px;
  font-weight: bold;
  color: ${props => props.color}; // Use color prop
  text-decoration: none;
  opacity: 0.5;
  z-index: 3;
  transition: color 0.3s ease, transform 0.3s ease;
  cursor: pointer;
`;

// Buttons
const StoreButton = styled.button`
  color: ${props => props.textColor}; // Use textColor prop
  z-index: 100;
  border-radius: 50px 0px 0px 500px;
  background-color: ${props => props.bgColor}; // Use bgColor prop
  border: none;
  padding: 30px;
  font-family: 'Vazir', 'Playfair Display', serif;
  text-align: center;

  &:hover {
    background-color: #9B59B6;
    transform: scale(1.05);
  }

  &:active {
    background-color: #8E44AD;
    transform: scale(1);
  }
`;

const AboutButton = styled.button`
  color: white;
  z-index: 100;
  border-radius: 0px 50px 500px 0px;
  background-color: ${props => props.bgColor}; // Use bgColor prop
  text-align: center;
  border: none;
  font-family: 'Vazir', 'Playfair Display', serif;
  padding: 30px;
`;

const TextLeft = styled.div`
  font-size: 120px;
  font-weight: bold;
  color: ${props => props.color}; // Use color prop
  z-index: 1;
  opacity: 0;
  animation: ${fadeIn} 2s ease-in-out forwards;
  animation-delay: 0.5s;
  cursor: pointer;
  font-family: 'Times New Roman', Times, serif;
  transition: transform 0.4s ease, color 0.3s ease, text-shadow 0.4s ease;
`;

const TextRight = styled.div`
  font-size: 120px;
  font-weight: bold;
  color: ${props => props.color}; // Use color prop
  z-index: 1;
  opacity: 0;
  animation: ${fadeIn} 2s ease-in-out forwards;
  animation-delay: 1s;
  font-family: 'Times New Roman', Times, serif;
  transition: transform 0.4s ease, color 0.3s ease, text-shadow 0.4s ease;
`;

export default Landing;
