import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { colorContext } from '../../Context/ColorContextProvider';



const About = () => {

  const { config } = useContext(colorContext);

  const [show, setShow] = useState(false);
  const [show2, setShow2] = useState(false);
  const [show3, setShow3] = useState(false);
  const [show4, setShow4] = useState(false);

  return (
    <Container theme={config.about}>
      <AboutSection theme={config.about}>
        <h1>درباره ما / سوالات متداول</h1>
        <h2>
          شکلات‌های دست‌ساز، سالم و سفارشی با <span>"نام فروشگاه"</span>
        </h2>
        <p>
          در فروشگاه آنلاین شکلات ما، شما می‌توانید شکلات‌های دست‌ساز و سالم با طعم‌های مختلف و با
          استفاده از بهترین مواد اولیه پیدا کنید. ما به تولید شکلات‌هایی با کیفیت بالا و طعمی منحصر به
          فرد اعتقاد داریم. تمامی محصولات ما ارگانیک و بدون مواد افزودنی مضر هستند تا شما تجربه‌ای
          خوشمزه و سالم را تجربه کنید. علاوه بر این، شما می‌توانید شکلات‌ها را به شکل بسته‌بندی‌های
          هدیه سفارشی، مناسب برای مناسبت‌های خاص مانند تولدها، اعیاد و جشن‌ها سفارش دهید.
        </p>
      </AboutSection>

      <FAQSection theme={config.about}>
        <Dropdown theme={config.about} onClick={() => setShow(!show)}>
          <h1>آیا شکلات‌های شما سالم و ارگانیک هستند؟</h1>
          <Answer theme={config.about} show={show}>
            <p>
              بله، تمامی شکلات‌های ما با استفاده از مواد ارگانیک و طبیعی تولید می‌شوند. ما به سلامت شما
              اهمیت می‌دهیم و از هیچ‌گونه مواد شیمیایی یا افزودنی‌های مضر استفاده نمی‌کنیم.
            </p>
          </Answer>
        </Dropdown>

        <Dropdown theme={config.about} onClick={() => setShow2(!show2)}>
          <h1>آیا می‌توانم شکلات‌ها را به صورت بسته‌بندی‌های هدیه سفارش دهم؟</h1>
          <Answer theme={config.about} show={show2}>
            <p>
              بله، شما می‌توانید شکلات‌ها را در بسته‌بندی‌های هدیه سفارشی دریافت کنید. ما انواع
              بسته‌بندی‌های شیک و جذاب برای مناسبت‌های مختلف مثل تولد، اعیاد و جشن‌ها ارائه می‌دهیم.
            </p>
          </Answer>
        </Dropdown>

        <Dropdown theme={config.about} onClick={() => setShow3(!show3)}>
          <h1>آیا شکلات‌های شما برای گیاه‌خواران مناسب هستند؟</h1>
          <Answer theme={config.about} show={show3}>
            <p>
              بله، بسیاری از شکلات‌های ما به طور خاص برای گیاه‌خواران تولید شده‌اند. ما به شما این
              اطمینان را می‌دهیم که می‌توانید با خیال راحت از شکلات‌های ما لذت ببرید، بدون اینکه نگران
              مواد غیرمجاز باشید.
            </p>
          </Answer>
        </Dropdown>

        <Dropdown theme={config.about} onClick={() => setShow4(!show4)}>
          <h1>آیا امکان بازگشت محصولات وجود دارد؟</h1>
          <Answer theme={config.about} show={show4}>
            <p>
              بله، در صورتی که مشکلی با سفارش خود داشته باشید، می‌توانید درخواست بازگشت محصول دهید. ما
              تضمین می‌کنیم که تجربه خریدی رضایت‌بخش را برای شما فراهم کنیم.
            </p>
          </Answer>
        </Dropdown>
      </FAQSection>
    </Container>
  );
};


