  width: 100%;
  height: fit-content;
  background-color: ${({ theme }) => theme.bg};
  padding-top: 50px;
  text-align: right;
  overflow: hidden;
  padding-bottom: 70px;
  margin-top: 100px;
  display: flex;
  flex-direction: row;


  @media screen and (max-width: 991px) {
    padding-top: 20px;
    margin-top: 50px;
    display: flex;
   flex-direction: column;

  }
`;

const AboutSection = styled.div`
  width: 100%;
  height: fit-content;
  word-wrap:wrap;
  // background-color: ${({ theme }) => theme.aboutSection};

  h1 {
    font-size: 14px;
    color: ${({ theme }) => theme.aboutSectionH1};
    padding: 20px 0px 0px 20px;
    line-height: 0px;

    @media screen and (max-width: 991px) {
      font-size: 12px;
      padding: 20px 50px 0px 20px;
    }
  }

  h2 {
    font-size: 33px;
    color: ${({ theme }) => theme.aboutSectionH2};
    padding: 20px 0px 0px 20px;
    line-height: 50px;

    @media screen and (max-width: 991px) {
      font-size: 24px;
    padding: 20px 50px 0px 20px;

    }
  }

  p {
    padding: 20px 0px 70px 70px;
    font-size: 15px;
    color: ${({ theme }) => theme.aboutSectionP};
    line-height: 35px;

    @media screen and (max-width: 991px) {
      padding: 20px 50px 0px 20px; 
      font-size: 13px;
      line-height: 1.8;
    }
  }
`;

const FAQSection = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 18px;
  transition: 1.5s ease;
  // background-color: ${({ theme }) => theme.faq};
  padding-top: 25px;
  padding-bottom: 25px;
  flex-wrap: wrap;
`;

const Dropdown = styled.div`
  width: 90%;
  height: fit-content;
  background-color: ${({ theme }) => theme.dropDown};
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 9px;
  transition: all 0.1s ease-in;
  cursor: pointer;

  h1 {
    color: ${({ theme }) => theme.dropdownH1};
    font-size: 15px;
    padding: 30px;
    line-height: 25px;
    text-align: center;
    margin: auto;
    transition: all 0.3s ease-in;
    display: block;
    width: 100%;

    &:hover {
      color: ${({ theme }) => theme.dropDownH1};
    }

    @media screen and (max-width: 991px) {
      font-size: 14px;
      padding: 8px;
    }
  }

  @media screen and (max-width: 991px) {
    width: 80%;
    padding: 10px;
  }
`;

const Answer = styled.div`
  max-height: ${({ show }) => (show ? '500px' : '0')};
  opacity: ${({ show }) => (show ? '1' : '0')};
  overflow: hidden;
  transition: max-height 0.6s ease-in-out, padding 0.6s ease-in-out, opacity 0.3s ease-in-out;
  padding: ${({ show }) => (show ? '10px' : '0 10px')};
  background-color: ${({ theme }) => theme.answer};
  border-redius: 10px;
  p {
    color: ${({ theme }) => theme.answerP};
    text-align: center;
    line-height: 25px;
  }

    @media screen and (max-width: 375px) {
    font-size: 14px;
  }
`;


export default About;
