import React, { useContext } from 'react';
import styled from 'styled-components';
import OfferingsSchema from './OfferSchema';

import { colorContext } from '../../Context/ColorContextProvider';


const data = {

    customChocolates: {
        title: 'شکلات سفارشی',
        content: 'شکلات‌های دست‌ساز با طعم و طرحی که خودتان انتخاب می‌کنید. از شکلات‌های کلاسیک تا طعم‌های جدید، هر چیزی که می‌خواهید در ویولا پیدا خواهید کرد.',
        path: '/store'
    },
    giftBoxes: {
        title: 'باکس‌های هدیه شکلاتی',
        content: 'باکس‌های شکلاتی زیبا و لوکس برای مناسبت‌های خاص. با بسته‌بندی‌های شیک و متنوع، این هدیه‌ها هر کسی را خوشحال می‌کنند.',
        path: '/store'
    },
    seasonalSpecials: {
        title: 'شکلات‌های فصلی ویژه',
        content: 'شکلات‌هایی با طعم‌های منحصر به فرد برای هر فصل و تعطیلات. طراحی‌های فصلی ویژه که طعمی تازه و شاداب به هر مناسبت می‌بخشند.',
        path: '/store'
    },
    chocolateArrangements: {
        title: 'آرایش‌های شکلاتی',
        content: 'آرایش‌های شکلاتی جذاب و هنری برای هدیه دادن. تجربه‌ای متفاوت با استفاده از شکلات به عنوان هدیه‌ای شخصی و خاص.',
        path: '/store'
    },
    chocolateMasterclasses: {
        title: 'کارگاه‌های تخصصی شکلات',
        content: 'در کارگاه‌های شکلات‌سازی ویولا، یاد می‌گیرید چطور شکلات‌های حرفه‌ای بسازید. یک فرصت عالی برای یادگیری و لذت بردن از فرایند شکلات‌سازی.',
        path: '/store'
    },
    personalizedTreats: {
        title: 'شکلات‌های شخصی‌سازی شده',
        content: 'شکلات‌های اختصاصی به سبک شما. این شکلات‌ها برای هدیه دادن به عزیزان شما یا برای خودتان به یادماندنی و خاص خواهند بود.',
        path: '/store'
    }
};

const description = '>در ویولا، شکلات‌های سفارشی و خاص به سلیقه شما طراحی می‌شود. ما شکلات‌های دست‌ساز و منحصر به فردی داریم که می‌تواند هر مناسبت را خاص کند.';



const OffersComponent = () => {

    const { config } = useContext(colorContext);


    return (
        <Container theme={config}>
            <Introduction theme={config}>
                <h1>خدمات ما</h1>
                <h2>چه چیزی ارائه می‌دهیم؟</h2>
                <p>{description}</p>
            </Introduction>
            <Offers theme={config}>
                <OfferingsSchema
                    imgSrc={data.customChocolates.img}
                    title={data.customChocolates.title}
                    content={data.customChocolates.content}
                    goto={data.customChocolates.path}
                />
                <OfferingsSchema
                    imgSrc={data.giftBoxes.img}
                    title={data.giftBoxes.title}
                    content={data.giftBoxes.content}
                    goto={data.giftBoxes.path}
                />
                <OfferingsSchema
                    imgSrc={data.seasonalSpecials.img}
                    title={data.seasonalSpecials.title}
                    content={data.seasonalSpecials.content}
                    goto={data.seasonalSpecials.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateBouquets.img}
                    title={data.chocolateBouquets.title}
                    content={data.chocolateBouquets.content}
                    goto={data.chocolateBouquets.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateWorkshops.img}
                    title={data.chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
                <OfferingsSchema
                    imgSrc={data.chocolateWorkshops.img}
                    title={data.chocolateWorkshops.title}
                    content={data.chocolateWorkshops.content}
                    goto={data.chocolateWorkshops.path}
                />
            </Offers>
        </Container>
    );
};

export default OffersComponent;

export const Container = styled.div`
    width: 100%;
    height: fit-content;
    /* background-color: ${({ theme }) => theme.bg}; */
    background-color: ${({ theme }) => theme.mainBg};
    //hello
    padding-top: 100px;
    padding-bottom: 70px;
    overflow: hidden;
    /* background-color: red; */


    @media screen and (max-width: 991px) {
        margin-top: 50px;
    }

`;

export const Introduction = styled.div`
    width: 100%;
    height: fit-content;
    /* background-color: ${({ theme }) => theme.introduction}; */
    margin-bottom: 100px;
    text-align: right;

    h1 {
        padding: 30px 80px 0px 0px;
        font-size: 45px;
        /* color: ${({ theme }) => theme.introH1}; */
        color: ${({ theme }) => theme.titile1};
    }

    h2 {
        padding: 20px 80px 10px 0px;
        font-size: 28px;
        /* color: ${({ theme }) => theme.introH2}; */
        color: ${({ theme }) => theme.titile2};
    }

    p {
        padding: 0px 80px 10px 0px;
        font-size: 17px;
        /* color: ${({ theme }) => theme.introP}; */
        color: ${({ theme }) => theme.description};
    }

    @media screen and (max-width: 991px) {

        margin-bottom: 0px;

        h1 {
            padding: 10px 30px 0px 0px ;
            font-size: 32px;
        }

        h2 {
            padding: 10px 30px 0px 0px ;
            font-size: 22px;
        }

        p {
            padding: 10px 30px 0px 0px ;
            font-size: 16px;
            word-wrap: wrap;
            width: 80%;
            // background-color: red;
            margin-left: auto;
        }
    }
    
`;

export const Offers = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 18px;
    transition: 1.5s ease;
    margin-top: 40px;
    /* background-color: ${({ theme }) => theme.offers}; */
    background-color: ${({ theme }) => theme.mainBg};

    @media screen and (min-width: 991px) {
        width: 90%;
        margin: auto;
        flex-wrap: wrap;
        flex-direction: row;
        justify-content: space-between;
    }
    @media screen and (max-width: 375px) {
        gap: 10px;

    //  div {
    //     width: 90%;
    //     padding: 15px;
    //     margin: 10px 0;
    //     }
    }
`;
